css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://userstyles.org/users/") , url-prefix("https://userstyles.org/users/") {

/* table format */
.author-styles {border-collapse: collapse;}
.author-styles .numeric-value {text-align: center !important;} /*center text of install counts*/

/* border width table cell */
.author-styles,
.author-styles td,
.author-styles th,
.author-styles td {border: 1px solid #888;}

/* Add caption to first TH empty cell */
.author-styles > THEAD > TR > TH:first-child::before {content: "Style Name" }

/* Table headings, style name table cells */
.author-styles th + th,
.author-styles tr > td:first-of-type {background: #E1EEEE}

.author-styles .obsolete {background: #DDD !important }

/* colored rating cells */
.author-styles .image-value {overflow: hidden;}

.author-styles .good-average-rating {outline: 30em solid #BDE9C2; background: #BDE9C2;}
.author-styles .ok-average-rating   {outline: 30em solid #FEFFBA;}
.author-styles .bad-average-rating  {outline: 30em solid #F4A8AB; background: #F4A8AB;}


}
