css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {

/*body, .notification.first_notification {
  background: black !important;
  border-radius: 0px !important;
}*/

/*.notification_type_icon.like_icon {background-image: none !important;}*/

.notification_type_icon.like_icon {
  border-top: 10px solid rgba(255,0,0, 1.25) !important;
  border-right: solid 10px rgba(255,255,255, 1.0) !important;
  border-bottom: 10px solid rgba(255,255,255, 1.0) !important;
  border-left: 10px solid rgba(255,0,0, 1.0) !important;
height: 0px !important;
width: 0px !important;
margin-top: -5px !important;
border-radius: 10px !important;
  -moz-transform: rotate(45deg) !important;
  background-color: blue !important;
}
.notification_type_icon.like_icon:before {
  position: absolute !important;
  content: "" !important;
   left: -10px !important;
  top: 0 !important;
  width: 20px !important;
  height: 0px !important;
  background: rgba(0,0,255, .50) !important;
  -moz-border-radius: 50px 50px 0 0 !important;
  border-radius: 50px 50px 50px 50px !important;
  border-bottom: solid thin rgba(0,0,0, 1.75) !important;
  margin-top: -.5px !important;
  margin-left: -10px !important;
  left: 0 !important;
  background: rgba(255,255,255, 1.0) !important;
 -moz-transform: rotate(-45deg) !important;
}
.notification_type_icon.like_icon:after {
  position: absolute !important;
  content: "" !important;
   left: 10px !important;
  top: 0 !important;
  width: 5px !important;
  height: 5px !important;
  background: rgba(0,0,255, .50) !important;
  -moz-border-radius: 50px 50px 0 0 !important;
  border-radius: 50px 50px 50px 50px !important;
  border: solid thin rgba(0,0,0, 1.0) !important;
  margin-top: -2px !important;
  margin-left: -7px !important;
  left: 0 !important;
  background: rgba(255,255,255, 1.0) !important;
  -moz-transform: rotate(45deg) !important;
  -moz-transform-origin: 100% 100% !important;
}
.selection_nipple.white {
   width: 24px !important;
   height: 12px !important;
   background: rgba(255,255,255, 1.0) !important;
   -moz-border-radius: 50px 50px 0 0 !important;
   border-radius: 0px 0px 50px 50px !important;
   border-top: solid 1.5px black !important;
   margin-top: -2px !important;
}
 .selection_nipple:not([class="selection_nipple white"]) {
   width: 0px !important;
   height: 0px !important;
   margin-top: -2px !important;
   background: rgba(255,255,255, 1.0) !important;
   -moz-border-radius: 50px 50px 0 0 !important;
   border-radius: 0px 0px 50px 50px !important;
   border-top: solid 1.5px black !important;
  border-bottom: solid 11px rgba(255,255,255,.0)  !important;
}

.selection_nipple.white:before,
.selection_nipple:not([class="selection_nipple white"]):before {
   position: absolute !important;
   content: "" !important;
   top:  -12px !important;
   left: 0px !important;
   width: 24px !important;
   height: 10px !important;
   border-bottom: solid 1.5px black !important;
   background: rgba(255,0,0, 1.0) !important;
   -moz-border-radius: 50px 50px 0 0 !important;
   border-radius: 50px 50px 0 0 !important;
   border-bottom: solid 2px black !important;
}
.selection_nipple:not([class="selection_nipple white"]):before {
   left: -12px !important;
}

.selection_nipple.white:after,
.selection_nipple:not([class="selection_nipple white"]):after {
   position: absolute !important;
   content: "" !important;
   width: 6px !important;
   height: 6px !important;
   left: 2px !important;
   top: -4px !important;
   margin-top: -1px !important;
   margin-left: 0px !important;
   border: solid 2px rgba(0,0,0, 1.0) !important;
   background: rgba(255,255,255, 1.0) !important;
   -moz-border-radius: 50px 50px 0 0 !important;
   border-radius: 50px 50px 50px 50px !important;
}
.selection_nipple.white:after {
  margin-top: -2px !important;
  margin-left: 5px !important;
}
.selection_nipple:not([class="selection_nipple white"]):after {
   margin-top: -2px !important;
   margin-left: -7px !important;
}}
