css:
#msg_box{position: absolute;left: 50%;right: 50%;top: 22%;width: 350px;height: 320px;margin: 0 0 0 -185px;padding: 5px;background-color: #000000;color: #FF0000;border: solid 2px #FFF;text-align: justify;font-family: "Lucida Grande", Sans-Serif;font-size: 8.5pt;Z-index: 5;}
#site_title{color: #FF0000;margin: 0 auto 1.5% auto;width: 100%;background-color: #B40404;display: block;overflow: hidden;}
#site_title a:visited, #site_title a:active, #site_title a:link, #footer_main a:visited, #footer_main a:active, #footer_main a:link, .nerdz_comments a:hover, .nerdz_comments a:visited, .nerdz_comments a:active, .nerdz_comments a:link, .page th a:hover, .page th a:visited, .page th a:active, .page th a:link{color: #000;font-weight: bold;text-decoration: none;cursor: pointer;}
#title_left{display: inline;float: left;font-size: 20pt;margin: 0.5%;}
#title_right{display: inline;float: right;margin: 0.5% 1% 0.5% 0.5%;}
#main{clear: both;display: block;margin: auto;overflow: hidden;width: 97%;}
#left_col{float: left;font-size: 9pt;vertical-align: top;height: 100%;margin-right: 1%;display: inline;overflow: auto;}
#center_col{width: 72%;height: 100%;vertical-align: top;float:left;display: inline;margin: auto;margin-left: 5px;margin-right: 5px;border-left: 1px dashed #808080;border-right: 1px dashed #808080;padding-left: 5px;padding-right: 5px;}
#right_col{height: 100%;width: 12%;vertical-align:top;float: right;display: inline;text-align: left;margin-left: -0.5%;}
#left_col ul, #right_col ul{list-style-type: square;list-style-position: inside;border: 0;padding: 0;margin: 0;}
#footer_main{background-color: #B40404;border-top: 1px 79B30B solid;bottom: 0px;clear: both;display: block;height: 24px;overflow: hidden;width: 100%;position: fixed;}
#footer_left{position: fixed;display: inline;bottom: 2px;left: 2px;text-align: left;}
#notify,#share,#chat_ico,#stats_bar,#bug_bar{position: fixed;bottom:0px;right: 0px;background-color: #B40404;padding-top: 4px;padding-bottom: 1px;font-weight: bold;color: #000;margin: 0%;border-left: solid 1.5px #000;height: 19px;width: 60px;text-align:center;}
#chat_ico{left: 155px;width: 30px;cursor: pointer;border-right: solid 1.5px #000;}
#stats_bar{left: 216px;width: 33%;font-size: 11px;border-right: solid 1.5px #000;}
#bug_bar{left: 186px;width: 30px;border-right: solid 1.5px #000;}
#chat_tab{float: left;background-color: #000;color: #FFF;padding: 1px;margin: auto;width: 99%;overflow-x: auto;}
#chat_tab li{background-color:#FFF;display:inline;}
#share{right: 61px;font-size: 8.5pt;}
#notify_list{position: fixed;display: block;bottom: 24px;right: 0px;background-color: #000;border: 1px dashed #FFF;}
.page{width: 99%;margin: auto;}
.page table{margin: auto;width: 100%;}
.page .intable{margin-top: 3%;width: 100%;margin-bottom: 3%;}
.page th, .title{text-align: center;vertical-align: middle;width: 10%;background-color: #fff;color: #000;border-radius: 2.5px;font-weight: bold;}
.nerdz_message, .nerdz_comments{width: 99%;background-color: #000;border-radius: 2.7px;font-size: 8.7pt;overflow: hidden;padding: 2px;border: 1px #B40404 solid;}
.nerdz_comments{border-color: #FF0000;background-color: #BDBDBD;color: #000; margin-bottom: 8px;}
.nerdz_message_no_sub_border{border-left: 1px #FF0000 solid;border-right: 1px #FF0000 solid;border-top: 1px #FF0000 solid;padding: 2px;width: 99%;background-color: #000;border-radius: 2.5px;font-size: 8.7pt;overflow: hidden;margin: auto;}
.nerdz_message_no_top_border{border-left: 1px #FF0000 solid;border-right: 1px #FF0000 solid;border-bottom: 1px #FF0000 solid;padding: 2px;width: 99%;background-color: #000;border-radius: 2.5px;font-size: 8.7pt;overflow: hidden;margin: auto;}
.nerdz_date, .chatboxoptions{float:right;margin-right: 2px;}
.nerdz_from{font-weight: bolder;overflow: hidden;font-style: italic;margin-top: 3px;width: 100%;margin: auto;}
.nerdz_truncate{background-color: #FFF;text-align:center;width:132px;color:#000;font-weight: bold;}
.qu_main{margin-left: 3%;margin-right: 1%;width: 95%;border: solid #B40404 1px;}
.error{color: #FF0000;font-size: 8pt;font-weight: bold;}
.small{font-size: 6.5pt;}
.pro_light{color: #FF0000;padding-bottom: 2px;}
.chatlist{position: fixed;display: block;bottom: 24px;left: 1px;width: 184px;height: 290px;background-color: #AFAFAF;border: 1px dashed #FF0000;border-bottom-style: solid;overflow-y: auto;color: #FF0000;}
.chatbox{position: fixed;width: 185px;display:none;}
.chatboxhead{background-color: #000000;height: 24px;color: #FF0000;border: 1px dashed #FF0000;}
.chatboxblink{background-color: #176689;border-right:1px solid #176689;border-left:1px solid #176689;}
.chatboxcontent{font-size: 11px;color: #FF0000;height:185px;width:169px;overflow-y:auto;overflow-x:auto;padding:7px;border:1px solid #FFFFFF;border-top: none;background-color: #afafaf;line-height: 1.3em;}
.chatboxinput{padding: 5px;background-color: #ffffff;border-left:1px solid #cccccc;border-right:1px solid #cccccc;border-bottom:1px solid #cccccc;}
.chatboxtextarea{width: 167px;height: 44px;padding: 3px 0pt 3px 3px;border: 1px solid #B40404;margin: 1px;overflow:hidden;}
.chatboxtextareaselected{border: 2px solid #B40404;margin:0;}
.chatboxmessage{margin-left:1em;}
.chatboxinfo{margin-left:-1em;color:#B40404;}
.chatboxmessagefrom{margin-left:-1em;font-weight: bold;}
.chatboxoptions a{text-decoration: none;color: #B40404;font-weight:bold;}
.chatboxtitle{float: left;}
.qu_user{background-color:#333;}
.ytvid{width:80%;margin: auto;text-align:center;}
.ytvid iframe{border: 0px;}
.atilda{font-style: italic;}
a:hover, a:visited, a:active, a:link, .qu_user a:visited, .qu_user a:active, .qu_user a:link, .atilda{color: #B40404;cursor: pointer;text-decoration: none;}

label, .qu_ico{cursor: pointer;}
input, option, select, textarea, button{border: 0.5px #000 solid;margin: 0.4px;text-align: left;background-color: #FFF;color: #FF0000;border-radius: 2.5px;}
body{background-color: #1D1B1B;color: #fff;font-size: 9pt;font-family: "Lucida Grande", "Cantarell", Arial, Helvetica, Sans-serif;font-weight: lighter;width: 100%;height: 100%;margin: auto;Z-index: 0;}
html, #body{width: 100%;height: 100%;}
