css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {background: url(http://i.imgur.com/DJMXF.png) top left !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { background:#0c0e34 !important; border:5px #a14e06 solid}
#posts {background:transparent !important}
#logo {width:0px !important; height:0px !important; padding-left:310px !important; padding-top:70px !important; margin-top:0px !important; background-image:url(http://i.imgur.com/ge5ae.png) !important;}
.controls_section.selected.following {background:transparent !important}
.notification {background:#a14e06 !important}
#right_column li {background:#a14e06 !important; opacity:0.7;}
#right_column a {color:white !important; text-shadow: 0px 0px 0px transparent !important; opacity:0.7}
#right_column li.selected {opacity:1.0; background:url(http://i.imgur.com/uFEZl.png) !important; background-size: 100% 100%  !important;}
#right_column li.selected:hover {opacity:1.0}
#right_column li:hover {opacity:0.9}
#right_column li.forever_alone {opacity:1.0; background:url(http://i.imgur.com/uFEZl.png) !important; background-size: 100% 100%  !important;}
.notification  {border:0px !important}
.notification a{border:0px !important; text-decoration:underline !important}
.notification a:hover{border-bottom:1px #753b15 solid !important}
.notification blockquote {border-left:2px #753b15 solid  !important}
.permalink {background:transparent !important}
.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
IMG[src*='http://assets.tumblr.com/images/content_top_edit_form.png'], IMG[src*='http://assets.tumblr.com/images/content_bottom_edit_form.png']{display:none !important;}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px #a14e06 !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}}

@-moz-document url-prefix("http://www.tumblr.com/new"),url-prefix("http://www.tumblr.com/reblog") , url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/reblog") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding-top:20px !important; border-radius: 20px}}
