css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("evernote.com") {

#gwt-debug-noteTitle,.notebookName {
color :#111 !important; font-weight: bold !important;
}

.noteSnippetTitle {
color :#555 !important; font-size: 9pt !important;
}
.noteSnippet {
background: rgba(248,248,248,1) !important; background : -moz-linear-gradient(top,rgba(240, 240, 240,1),rgba(255, 255, 255,1)) !important; 
}


.noteSnippet > DIV > DIV {float: right !important;}
.noteSnippet > DIV > DIV + DIV {float: left !important;}

.noteSnippet:hover {
background: #eee !important;
}

.noteSnippetDate:before {content: 'Updated: ';}
.noteSnippetDate {font-weight: normal !important;}

.noteSnippetContent:before {content: '\a';white-space: pre;}

.noteSnippetContent {color: #777 !important; }

.selectedNoteSnippet .noteSnippetTitle {
color :#000 !important;

}

/*
Get rid of ads
*/


div[class="GPC4LG3CP"] > DIV+DIV+DIV+DIV { bottom: 0px !important;}

div[class="GPC4LG3CP"] > DIV+DIV+DIV {top: height: auto !important;bottom:30px !important;}

div[class="GPC4LG3CP"] > DIV:last-child { display: none !important;}


.selectedNoteSnippet{
background: #e6f1f2 !important
}
}
