css:
@-moz-document url("chrome://browser/content/browser.xul") {
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#downbarMainMenuButton, stack[style="background-image: url(chrome://downbar/skin/whiteToTransGrad.png);"], #downbarClearButton, .db_progress-remainder {
  background: none !important;
  margin: 0px 0px 0px -20px !important;
  background-image: none !important;
 }

#downbarMainMenuButton, #downbarClearButton {
  border: 0px solid ThreeDShadow !important;
  -moz-border-top-colors: ThreeDShadow !important;
  -moz-border-right-colors: ThreeDShadow !important;
  -moz-border-left-colors: ThreeDShadow !important;
  -moz-border-bottom-colors: ThreeDShadow !important;
  padding-bottom: 1px !important;
  padding-top: 1px !important;
}

#downbarMainMenuButton:hover, stack:hover[style="background-image: url(chrome://downbar/skin/whiteToTransGrad.png);"], #downbarClearButton:hover { 
  background-color: rgba(255,255,255,0.2) !important;
  border: 0px solid ThreeDShadow !important;
  -moz-border-top-colors: ThreeDShadow !important;
  -moz-border-right-colors: ThreeDShadow !important;
  -moz-border-left-colors: ThreeDShadow !important;
  -moz-border-bottom-colors: ThreeDShadow !important;
}

#downbarMainMenuButton>label,#downbarToMiniButton>label{-moz-binding:url("chrome://global/content/bindings/button.xml#button-image")!important;list-style-image:url(data:image/png;base64,)!important}

#downbarHolder {
border-top-width: 0px !important;
visibility: visible !important;
}

#downbarHolder[hidden="true"] {
-moz-transition-property: height, visibility;
height: 1px !important;
visibility: collapse !important;
}

.db_pauseStack,
.db_progressStack,
.db_finishedHbox,
.db_notdoneHbox {
background: transparent none !important;
background-clip: content-box !important;
background-image: -moz-linear-gradient(top,rgba(0,0,0,.05),rgba(0,0,0,0)) !important;
background-origin: content-box !important;
border-radius: 0 !important;
border: 0 !important;
height: 10px !important;
margin: 0 !important;
overflow: hidden !important;
padding: 0 !important;
text-shadow: 0 1px 0 rgba(255,255,255,.3) !important;
}

.db_pauseStack { background-color: rgba(255,255,255,0.5) !important;
padding: 2px !important;
max-width: 100px !important;}
.db_progressStack { background-color: rgba(255,255,255,0.5) !important;
padding: 2px !important;
max-width: 100px !important;}
.db_finishedHbox { background-color: rgba(255,255,255,1) !important;
padding: 2px !important;
max-width: 100px !important;}
.db_notdoneHbox { background-color: rgba(255,255,255,0.5) !important;
padding: 2px !important;
max-width: 100px !important;}

.db_progress-bar {
background-color: rgba(255,255,255,.2) !important;
box-shadow: 0px 0px 0px 0px rgba(0,0,0,.5) !important;
}
.db_progress-remainder {
background-color: rgba(50,50,50,.2) !important;
}
.db_progress-bar[minwidth="0"],
.db_progress-bar:not([minwidth]) {
box-shadow: none !important;
}

.db_filenameLabel {
color: black !important;
font-size: 12px !important;
font-weight: bold !important;
margin: -1px 0px 0px 0px !important;
}
.db_finishedHbox .db_filenameLabel {
margin-left: 0px !important;
}
.db_finishedIcon {
visibility: hidden !important;
}
.db_notdoneHbox > :last-child > :first-child {
visibility: collapse !important;
}
.db_progressIndicator {
text-shadow: none !important;
}

#_dlbar_finTipContent,
#_dlbar_progTipContent,
#_dlbar_finTipMiddle,
#_dlbar_progTipMiddle,
#_dlbar_finTipImgPreviewBox,
#_dlbar_progTipImgPreviewBox,
#_dlbar_finTipIcon,
#_dlbar_progTipIcon {
background: transparent !important;
border: 0px !important;
list-style-image: none !important;
margin: 0 !important;
padding: 0 !important;
}
.db_tootipTitle,
.db_tootipText {
color: white !important;
text-shadow: 0 1px 0 rgba(0,0,0,.4) !important;
}
}
