css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bddebian.com") {
  html, input#searchbox {
    background-color: #302F2F !important;
  }
  html, input#searchbox, .sidebar p, .sidebar ul {
    color: #ccc !important;
    font-family: Loma,Verdana,Geneva,Tahoma,sans-serif !important;
  } 

    .header, .sidebar, .pageheader .actions li:hover, .pagedate, 
    .pagecopyright, .pagelicense, .copyright_assignment_notice /* 
    Used in `.templates/editpage.tmpl'. */ {
      background-color: #212020 !important;
      /* was #f0f0f0, based on 302F2F */
    }

    .pageheader .actions {
      background-color: #181717 !important; /* was #e7e7e7 */
      padding: 0em 0.5em 0.8em 0.5em !important;
      /* changed 0.3 to 0.8 to make background go down to border */
      border-top: 1px solid #636262 !important;
      /* was #999999, added #333 to base (302f2f) */
    }

    .pageheader .actions li { border: none !important; }

    .pageheader .actions li:hover {
      border: 1px solid #797979 !important; /* #999999 */
      border-top: 1px solid #212020 !important; /* #f0f0f0 */
      border-radius: 0 0 0.8em 0.8em !important;
      -moz-border-radius: 0 0 0.8em 0.8em !important;
      -webkit-border-radius: 0 0 0.8em 0.8em !important;
    }

    .sidebar { border: 1px solid #aaa !important; /*was black*/ }

    a {
      text-decoration: none !important;
      color: #3c69a4 !important;
    }
    a:hover {
      border-bottom: 1px dotted #3d72a4 !important;
      /* was blue; */
    }

    hr { background-color: #aaa !important; }

  #pageinfo {
    border-top: 1px solid #aaa !important; /*#000;*/
  }

  input {
    padding: 0 !important;
    /* fixes webkit that breaks navigation */
  }
/*
