css:
@-moz-document domain("news.163.com")
{
/*头部广告*/
.top-gg-area,.ep-area,
/*右栏*/
.ep-content-side,
/*正文下的聚合阅读*/
.extra-tag,
/*回到新闻/网易首页*/
.ep-returnlink,
/*关键词阅读*/
#js-epTabNews,
/*扩展阅读*/
.ep-tjiframe,
/*正文底部大菜单*/
#js_N_nav_footer,
/*空白间隔*/
.blank20,.blank9,.blank6,
/*正文底部广告等*/
.sharecommend-wrap,iframe[width="590"],.ep-keywords,#js-ep-bobo,img[width="262"]
{display: none !important;}

/*新闻首页去掉头部广告后压缩头部的空间*/
.wrapperbg{height:141px !important;}
/*新闻主页*/
h2.bigsize, h3.bigsize { font-family: "方正宋黑简体","黑体" !important;}
/*新闻正文页*/
/*左栏主框架*/
.ep-content-main {width:100% !important;}
/*左栏次框架*/
#endText{width:100% !important;}
.ep-content-bg {background:none !important;}
/*正文标题*/
h1.ep-h1{
  font-family: "方正宋黑简体","黑体" !important;
  font-size: 32px !important; 
  line-height: 1.5em !important;
  text-align:center;
}
/*正文标题下发布时间、来源、参与人数、分享到*/
.ep-info {float:right !important;}

p.ep-summary {
  width:80% !important;
  text-align:center;
  margin:0 auto !important;
}
/*调整正文中内嵌视频*/
.video {width:70% !important;height:504px !important;margin:auto;}
#endText .video-info *{font-size:16px !important;}
/*正文段落*/
#endText p { 
  font-size: 26px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important;
}

.nph_photo_view{width: 960px !important;}

}

@-moz-document domain("view.163.com") {
.colR {
  display: none !important;}
.areabg1 {
  background: none !important;}
.colLM {
  width:960px !important;}
.feed-text p {
  font-size: 24px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important; }
}
