css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.washingtonmonthly.com") {

div#page {
	width: 1024px !important;
}

ul#nav-main {
	width: 1020px !important;
}


div#content {
	width: 624px !important;

}

.article div#content div.article,
.blog div#content div.article {
	padding-right: 20px !important;
}

div#content div.article p,
div#content div.article ul,
div#content div.article ol {
	font: 16px/24px georgia, times, serif !important;
}

div#comments {
	font-family: georgia, times, serif !important;
  	margin-right: 20px !important;

}

ul#comment-list li {
	padding-top: 10px !important;
	margin-bottom: 5px !important;
}

div#comments ul#comment-list li p {
	font-size: 16px !important;
	line-height: 24px !important;
	margin-bottom: 10px !important;
}

form#post-comment input.text,
form#post-comment textarea {
	width: 598px !important;
}

}
