css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.se"), domain("www.thepiratebay.se/"),
url("https://thepiratebay.se/") 

{

a:link    { color: #aaa !important}
a:visited { color: #888 !important}
a:active  { color: #999 !important}
span  {color: gray !important}
.entrybody p {color: #999 !important}
.entrybody ul {color: #666 !important}
.entrymeta {color: #777 !important}
.commenttext {color: #999 !important}
B {color: #999 !important}
LABEL {color: #999 !important}
DT {color: #999 !important}
DD {color: #999 !important}
TD  {color: #777 !important}

a {border: none !important}

BODY {background: #000 !important; color: #aaa !important; margin:0!important; padding:0!important; }


h1 a{background: url(http://img651.imageshack.us/img651/3111/529pxthepiratebaylogosv.png) top center no-repeat #000 !important; height: 310px !important} 

H2 {background: #111 !important; -moz-border-radius: 0px !important; border: solid 0px #222 !important; color: gray !important;}

#TPBlogo{background:url(http://img263.imageshack.us/img263/3111/529pxthepiratebaylogosv.png) no-repeat !important; width: 0px !important; height:85px !important; padding-left: 75px!important}

IMG[border="0"][alt=""][src="https://static.thepiratebay.org/img/icon-https.gif"] {display: none !important}

IMG[border="0"][alt=""][src="http://static.thepiratebay.org/img/blTrial.gif"] {display: none !important}

TH {background: #222 !important;}

.searchBox {
   color: #d0d0d0 !important;
   background: #0f0f1a !important;}

table#searchResult th, table#searchResult td  {border: solid 2px #111 !important}

#searchResult > TBODY > TR {background: #111 !important; border: solid 1px #111 !important}



#title {background: #222 !important; border: none !important}

#detailsframe { border: none !important;}

#details {background: #111 !important;}

.nfo {background: #444 !important; color: #000 !important; border: solid 1px #111 !important;}

IMG[border="0"][alt="Toolbar"][src="http://static.thepiratebay.org/img/new_toolbar.gif"] {display: none !important}

#commentsheader > H4 {color: gray !important;}

#comments dt { background: #222 !important;}

#filelistContainer {background: #000 !important;  border: solid 1px #555 !important; }

.byline {color: #777 !important;}

.comment {background: #444 !important; border: solid 6px #444 !important; color: black !important;}

DD, DT { border-bottom: solid 2px #444 !important}
#foot { display:none !important; }

.twitter-share-button { 
 display: none !important;}}
