css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allegro.pl") {

div#wrapper {
  margin: 10px 0 0px auto !important;
}

.search_panel_1 { 
  padding: 0 !important;
}

/* lewy nawigator */
div#naviLeft {
  margin: 0 !important;
  width: 230px !important;
  height: 100% !important;
  position: fixed;
  top: 0;
  left: 10px;
  overflow: auto;
}

#contentRight { width: 100% !important; }

.galleryWrap .gallery .galleryContainer div.img {
  z-index: 0 !important;
}
#pbpromopts {
  z-index: 1;
}

/* parametry przedmiotow */
div#paramsTopWrap form {
  position: fixed;
  top: 0;
  left: 244px;
  width: 450px;
}
.filterOptions .params_section {
  width: 49% !important;
  margin: 0 !important;
}

/* form kup teraz */
#siWrapper {
  position: fixed !important;
  top: 0;
  left: 0;
  width: 695px;
  margin-left: 0 !important;
}
/* dane firmy */
#companyData {
  border: 1px solid #CCC;
  margin-bottom: 8px;
  padding: 5px 6px 7px 6px;
  border-radius: 5px;
  background-color: #fff;
  z-index: 101;
}

#footerContentBox {
    opacity: 0.2;
}
#footerContentBox:hover {
    opacity: 1;
}

#showCatAdvert750x100, #isAdvert, .advContent  { display: none !important; }

}
