css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

*{text-shadow:none !important; color:#666666 !important;}{font:16px 'Trebuchet MS';
color:#666666;
}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_tumblelog, #dashboard_inbox, #dashboard_help, 







#dashboard_preferences, #dashboard_followers, #dashboard_members, #dashboard_tumblelog, #dashboard_inbox, #dashboard_help{background: url('http://i52.tinypic.com/9kmq6u.png') bottom left #E5DBCF no-repeat fixed !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#f9f9f9!important; background: rgba(105,105,105,0.3) !important;}
ol#posts li.notification{background-color: #f9f9f9 !important; border-bottom: 2px solid #9d92a5 !important; color: #f9f9f9 !important;opacity: 0.50;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#f9f9f9;opacity: 0.50;}
#nav .nav_item.active{background-color:#f9f9f9}
#left_column #posts .post .permalink{background-color:#f9f9f9;opacity: 0.50;}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#f9f9f9 !important;
background-image:none !important;
border:0px !important}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.notification_type_icon{display:none !important}
#left_column #posts .notification {
background-color:#f9f9f9 !important;
color:#666 !important;
border-bottom-color: #eee !important;opacity: 0.50;}
#new_post_notice_container{
color:#fff !important}
#new_post_notice_container a{
color:#fff !important}
ol#posts li.post.new_post {
background: #ffffff url('http://i54.tinypic.com/5thg1.png') center center no-repeat}
