css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://s4.zetaboards.com/SFFliveson/") 
{
body, #main_content, #side_l, #header, #side_r, #footer_l, #footer_r, #header_l, #header_r, #top, .drop_menu li, #logo, #submenu, #nav, #main, td, .post_sep, .h2right, th, blockquote, blockquote dl, #wrap, #fast-reply *
{
    background: black !important;
}

#main, #wrap
{
    max-width: 980px !important;
    margin: auto !important;
}

table[id="topic_viewer"]
{
    display: block !important;
    max-width: 1000px !important;
}

table[id="topic_viewer"] td.c_post
{
    display: block !important;
    max-width: 724px !important;
    overflow: auto !important;
}

#nav
{
    margin-top: 10px !important;
}

#foot_wrap
{
    margin: 0px !important;
    border-bottom: 1px white dashed !important;
}

blockquote
{
    padding: 0px !important;
}

blockquote dl
{
    border-bottom: 1px white dashed !important;
}

td, #logo
{
    border: 0px !important;
}

.c_postfoot, blockquote, #fast-reply dt
{
    border: 1px white dashed !important;
}

#fast-reply dd
{
    border-top: 0px !important;
    border-left: 1px white dashed !important;
    border-right: 1px white dashed !important;
    border-bottom: 1px white dashed !important;
}

canvas, #footer, #header
{
    display: none !important;
}

#main
{
    padding: 0px !important;
}

#fast-reply, .c_topicfoot
{
    width: 960px !important;
}

.category
{
    margin-top: 0px !important;
}

.topic-buttons, .cat-buttons
{
    margin: 0px !important;
    border-right: 1px white dashed !important;
    border-bottom: 1px white dashed !important;
    width: 460px !important;
    padding-right: 20px !important;
}

.cat-pages
{
    margin: 0px !important;
    margin-bottom: 20px !important;
    border-left: 1px white dashed !important;
    border-bottom: 1px white dashed !important;
    padding: 0px !important;
    width: 460px !important;
    height: 25px !important;
    padding-left: 20px !important;
}

#top, .h2right, #nav
{
    border-top: 1px white dashed !important;
    border-left: 1px white dashed !important;
    border-right: 1px white dashed !important;
    border-bottom: 0px !important;
}

td.c_postinfo
{
    border-left: 1px white dashed !important;
}

tr
{
    border-right: 1px white dashed !important;
    border-left: 1px white dashed !important;
}

#logo
{
    border-left: 1px white dashed !important;
    border-right: 1px white dashed !important;
    border-top: 0px !important;
}

td.c_user
{
    border-right: 1px white dashed !important;
}

#submenu, th
{
    border-left: 1px white dashed !important;
    border-right: 1px white dashed !important;
    border-top: 0px !important;
    border-bottom: 1px white dashed !important;
}

#submenu_bar
{
    display: none !important;
}

img[src="http://img694.imageshack.us/img694/8449/happening.png"]
{
    width: 0px !important;
    padding-left: 730px !important;
    height: 100px !important;
    background-image: url(data:image/png;base64,) !important;
}
}
