css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {







#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members{

background-image:url('http://i54.tinypic.com/334248z.jpg') !important;

background-repeat:repeat-x!important;

background-attachment:fixed;

background-position: top!important;

background-color:#030303 !important;

}



#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(10,10,10,0.5) !important;

}



#dashboard_edit_post{

background-color: #ffffff !important

opacity:0.6;

}







#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {    

background: none; }



#right_column .dashboard_nav_item {    

background: none;    

background-attachment: initial;    

background-position-x: 50%;    

background-position-y: 100%;    

background-origin: initial;    

background-clip: initial;    

background-color: initial;}



#right_column .dashboard_nav_item #hide_radar {

background-image: none;

background-repeat-x: no-repeat;

background-repeat-y: repeat;

background-attachment: initial;

background-position-x: 0%;

background-position-y: 0%;

background-origin: initial;

background-clip: initial;

background-color: transparent;}



#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(0,0,0,0.5);}



.post{opacity:1 !important; border-bottom:1px !important; color:#000}



.permalink{background-color: transparent !important;}



.nav_item_nipple{display:none !important}



.nav_menu{background-color:#245a70 !important; border-bottom:0px !important}



#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#2f5c88 !important;

background-image:none !important;

border:0px !important

}



#nav .nav_item .nav_menu {

border-bottom:0px !important

}



#nav .nav_item .nav_menu a {

color:#c18d6a !important;

border-top-color:#322115 !important;

}



#nav .nav_item .nav_menu a:hover {

color:#fbfbfb !important;

}



.dashboard_switch_blog_menu_header{

background-color:#1a436e !important;

color:#666 !important;

}



.dashboard_switch_blog_menu_item{

background-color:#fff !important;

color:#666 !important;

border:0px !important;



}



.dashboard_switch_blog_menu_footer{

background-color:#1a436e !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_wrapper{

background-color:#1a436e !important;

}



#new_post_notice_container{

color:#fff !important

}



#new_post_notice_container a{

color:#fff !important

}



#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current{

background-image:none !important

}



#footer{

background: rgba(10,10,10,0.6) !important;

background-image:none !important

}





ol#posts li.post.new_post {

background: #ffffff url('http://img178.imageshack.us/img178/3291/2uyhzlcjpg.png') center center no-repeat;

}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

width:0;

height:70px;

padding-right:250px;

opacity: 0;

}



.new_post_label{



font-size:10px !important;

padding-top:5px !important;

font-family:sans-serif !important;

font-style:bold !important;

text-transform: uppercase !important;

color:#222 !important;

letter-spacing:1px !important

}



#left_column #posts .notification .avatar {

border-color:transparent !important;

background-color:#1a436e !important;

opacity:0.4 !important;

}



#left_column #posts .notification .avatar:hover{

opacity:0.6 !important;

}



#left_column #posts .notification {

background-color:#1a436e !important;

color:#2ea6c9 !important;

border-bottom-color: #2e2719 !important

opacity:0.4 !important;

}



#left_column #posts .notification .block {

color:#2ea6c9 !important;

}

#left_column #posts .notification.alt {

background-color: #100e09 !important;

border-bottom-color: #2e2719 !important

opacity:0.4 !important;}



#left_column #posts .notification.last_notification, #left_column #posts .notification.single_notification {

border-bottom-color:#2ea6c9 !important;

}

#left_column #posts .notification a {

color:#3a87ad !important;

border-bottom:0px !important;

}



#left_column #posts .notification blockquote {

border-left-color:#eee !important;

}



#left_column #posts .notification .avatar {

border-color:transparent !important;

background-color:#fbfbfb !important;

opacity:0.6 !important;

}

#left_column #posts .notification .avatar:hover{

opacity:1 !important;

}



.notification_type_icon{display:none !important}



h1,h2,h3,h4,h5,h6{color:#407f95 !important}



.source_url, .tags {display: inline !important; white-space: normal !important;}



.source_url_gradient {display: none !important;}





}
