css:
/* This is based on FF3 black background for Vista by DigDug. This is the first time I used stylish and/or tried to customize Firefox. Some of this may not be necessary or as good as it can be. */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

menubar > menu, #toolbar-menubar, #urlbar, #searchbar, #find-field-container
{ 
  -moz-appearance: none !important;
  border: none !important;
}

navigator-throbber {
   color: #50A0A0 !important;
}

domi-panel > toolbox > toolbar { 
   background-color: #50A0A0 !important;
}

#urlbar-search-splitter, toolbar-splitter {
  -moz-appearance: none !important;
   background-color: black !important; 
   color: black !important;
   border: none !important;
}

toolbar {
  -moz-appearance: none !important;
   background-color: #305555 !important; 
   border: none  !important;
}

#nav-bar, toolbox {
  -moz-appearance: none !important;
  background-color: #305555 !important;
}

#browser, #appcontent, #content {
   background-color: black !important;
}
#appcontent, #sidebar-box {

   background-color: black !important;
}
#appcontent {
      padding-top: 2px ;
}

#sidebar-box.chromeclass-extrachrome {
   padding-top: 2px ;  
   margin-top: 2px; 
   background-color: #336666 !important;
}
#sidebar {
   padding-top: 2px !important;
   background-color: black !important;
}
sidebarheader,
#organizerScopeBar {
  -moz-appearance: none !important;
  background-color: #336666 !important;
  border-top: 2px #336666 !important;
  border-bottom: 4px #336666 !important;
  background-repeat: repeat-x !important;
  color: #88DDDD !important;
  border-color: black !important
}


#history-panel {
   color: #88DDDD !important;
   padding: 0px;
   background-color: #336666!important;
}

toolbox, toolbar  {
  border-width: 0 !important;
  -moz-appearance: none !important;
   background: #305555 !important;
  border: none !important;
}

#navigation-toolbar {
  -moz-appearance: none !important;
}

#placesToolbar {
  -moz-appearance: none !important;
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAABGdBTUEAALGPC/xhBQAAABh0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjIyt5EXfQAAACZJREFUGFdj+P//vwPDv3//HEGEEwOYi0aAZbFJYKqzA6ljoJgAACC0XZ3ZDZeDAAAAAElFTkSuQmCC) !important;
  background-repeat: repeat-x;
  height: 32px !important;
  color: white !important;
}

#PersonalToolbar {
  padding-top: 2px ;
  background-color: black !important
}

#personal-bookmarks {
  background-color: #336666 !important;
}

toolbar .toolbarbutton-text,
#content .tab-text,
.menubar-text {
  color: #AAFFFF !important;
}

#content .tabs-container {
  -moz-appearance: none !important;
  background-color: #122424 !important;
  border: none !important;
}
#content .tabs-alltabs-button { 
  background-color: #336666 !important;
  border-right:2px black !important;
  padding-left: 2px !important;
  padding-bottom: 2px !important;
  border: 0px;
  margin: 0px !important;
  margin-bottom: 2px !important;
}
#content .tabs-alltabs-button:hover { 
  background-color: #50A0A0 !important;
}

#content tab:not([selected="true"]) {
  -moz-opacity: 0.7 !important;
  padding: 0px;
}
#content tab:not([selected="true"]) .tab-text {
  -moz-opacity: 0.7 !important;
}
#content tab {
  border: 0px !important;
  padding: 0px !important;
  margin-top: 0px !important;
  margin-bottom: 2px !important;
  margin-left: 0px !important;
  margin-right: 0px !important;
  padding: 0px;
  -moz-border-radius-topleft: 0px !important;
  -moz-border-radius-topright: 0px !important;
  -moz-border-radius-bottomleft: 0px !important;
  -moz-border-radius-bottomright: 0px !important;
  background-color: #336666 !important;
}

#content tab[selected="true"] .tab-text {
  color: black !important;
  padding: 0px;
  -moz-outline: none !important;
  border: none !important;
}
#content tab[selected="true"]  {
  margin-top: 0px !important;
  margin-bottom: 2px !important;
  margin-left: 0px !important;
  margin-right: 0px !important;
  background-color: #50A0A0 !important;
}
/* the box around the tabs making the padding */
#content hbox, arrowsscrollbox, scrollbox, box {
  /* background-color: black !important; */
   padding: 0px !important; 
   margin-left: 0px !important;
  -moz-appearance: none !important;
   border: none !important;
}

/* investigation
tabsbrowser-arrowscrollbox{ 
   padding: 10px;
   margin: 0;
   color: white !important;
   background-color: white !important;
}
tabs-stack, tabbrowser-tabs, tabscontainer {
   padding: 0;
   margin: 0;
   color: white !important;
}
#tabs-bottom {
  -moz-appearance: none !important;
  display: none !important;
}
*/

#searchFilter, textbox,
#urlbar,
.searchbar-textbox {
  background-color: #FFFFEE !important;
  -moz-appearance: none !important;
  border: 1px black !important;
}

.searchbar-engine-button,
#identity-box {
  background-color: transparent !important;
  -moz-appearance: none !important;
  border: none !important;
  margin: 1px 0px 1px 0px !important;
  outline: none !important;
}

/*
.searchbar-textbox:hover,
.searchbar-textbox[focused="true"],
#urlbar:hover,
#urlbar[focused="true"],
#searchFilter:hover,
#searchFilter[focused="true"] {
  background-color: rgb(188,204,226) !important;
}
*/

/* Fix menu icons for CuteMenus */
.menu-iconic-left {
  min-width: 28px !important;
}

#searchModifiers {
  background-color: rgb(236,236,236) !important;
  border-bottom: 1px solid black !important;
  padding-right: 0px !important;
  -moz-margin-end: 0px !important;
}

#organizerScopeBar toolbarbutton[checked="true"] {
  -moz-appearance: none !important;
  background-color: white !important;
  background-image: none !important;
  -moz-border-radius: 3px !important;
}

#organizerScopeBar toolbarbutton[checked="true"] .toolbarbutton-text {
  color: black !important;
}

#organizerScopeBar label,
#organizerScopeBar .toolbarbutton-text {
  color: white;
}

#advancedSearch[collapsed="true"] + #searchActions {
  display: none !important;
}

#placesView tree,
#infoPane,
#bookmarks-view,
#historyTree {
  background-color: rgb(238,243,250) !important;
}

#splitter, splitter,
#contentSplitter,
#sidebar-splitter {
  width: 2px !important;
  height: 2px !important;
  min-width: 1px !important;
  min-height: 1px !important;
  background-color: black !important;
  border: none !important;
}

#contentSplitter grippy,
#splitter grippy {
  visibility: hidden !important;
}

#organizeButton {
  list-style-image: url(data:image/png;base64,) !important;
}

#viewMenu {
  list-style-image: url(data:image/png;base64,) !important;
}
#maintenanceButton {
  list-style-image: url(data:image/png;base64,) !important;
}

.textbox-input-closebutton,
.search-go-button {
  list-style-image: url(data:image/png;base64,) !important;
}

.textbox-input-closebutton,
.search-go-button {
  -moz-image-region: rect(0px,78px,20px,52px) !important;
  display: -moz-box !important;
  height: 20px !important;
  width: 26px !important;
  margin: 0px !important;
  padding: 0px !important;
}

statusbar, statusbarpanel {
 -moz-appearance: none !important;
  background-color: #163636!important;
  color: #50A0A0 !important;
  border: none !important;
}


#searchFilter[filtered="true"] .textbox-input-closebutton {
  -moz-image-region: rect(0px,104px,20px,78px) !important;
}

#searchFilter[filtered="true"] .textbox-input-closebutton:hover {
  -moz-image-region: rect(20px,52px,40px,26px) !important;
}

#searchFilter[filtered="true"] .textbox-input-closebutton:hover:active {
  -moz-image-region: rect(40px,52px,60px,26px) !important;
}
