@-moz-document domain("s1.bitefight.ru"),
domain("s2.bitefight.ru"),
domain("s3.bitefight.ru"),
domain("s4.bitefight.ru"),
domain("s5.bitefight.ru"),
domain("s6.bitefight.ru"),
domain("s7.bitefight.ru"),
domain("s8.bitefight.ru"),
domain("s9.bitefight.ru"),
domain("s10.bitefight.ru"),
domain("s11.bitefight.ru"),
domain("s12.bitefight.ru"),
domain("s13.bitefight.ru"),
domain("s14.bitefight.ru"),
domain("s15.bitefight.ru"),
domain("s16.bitefight.ru")
{

body {
 background-image: url(http://bf.logserver.net/skin/vampire/page-bg.jpg)!important;
    font-family:Arial, Helvetica, sans-serif;
    color: #b09795;
    font-size:12px;
    text-align: center;
}
#header {
	background:url(http://bf.logserver.net/skin/vampire/header-bg.png)!important;
position: absolute;
height:250px;
	width:1014px;
}
#feuerheader {background:none !important;}
#container {
	background:url("http://bf.logserver.net/skin/vampire/container.png")repeat-y scroll 25px 0 transparent !important;
	height:auto !important;
	margin:0 auto;
	height:auto;
	height:515px;
	min-height:515px;
	padding:0;
	position:relative;
	width:1014px;
}

h2 {
	background:url("http://bf.logserver.net/skin/vampire/h2.png") no-repeat scroll 50% 0 transparent !important;
	color:#cad0d6;
	color:#BEC1C3;
	font-family:"Times New Roman", Times, serif;
	font-size:18px;
	font-weight:bold;
	height:33px;
	line-height:33px;
	margin-bottom:2px;
	margin-top:0;
	overflow:hidden;
	padding-left:0;
	text-align:left;
}

#content td {background:none !important;}

#content #hideoutPic td {background:none !important;}

#items .ui-state-default,#items .ui-widget-content .ui-state-default,#items .ui-widget-header .ui-state-default {
    background: url("http://bf.logserver.net/skin/vampire/h2.png") no-repeat scroll 50% 0 transparent !important;
    cursor: pointer;
}

#items .ui-state-active,#items .ui-widget-content .ui-state-active,#items .ui-widget-header .ui-state-active {
    background: url("http://bf.logserver.net/skin/vampire/h2.png") no-repeat scroll 50% 0 transparent !important;
    cursor: pointer;
}
}
