css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), 
url-prefix("http://www.tumblr.com/tumblelog"),
url-prefix("http://www.tumblr.com/inbox"),
url-prefix("http://www.tumblr.com/tagged"),
url-prefix("http://www.tumblr.com/likes"),
url-prefix("http://tumblr.com/dashboard"), 
url-prefix("http://tumblr.com/tumblelog"),
url-prefix("http://tumblr.com/inbox"),
url-prefix("http://tumblr.com/tagged"),
url-prefix("http://tumblr.com/likes")
{

body {
   background: url(http://i.imgur.com/E6Ulg.jpg) center left no-repeat fixed !important;
   background-size: /*[[bgsize]]*/ ;
}
#logo {
   z-index: 100;
   position:relative; 
   top:-35px; 
   left:440px; 
   width:0px !important; 
   height:0px !important; 
   padding-left:210px !important; 
   padding-top:50px !important; 
   margin-top:0px !important; 
   background-image:url(http://i.imgur.com/vn53A.png) !important;
 }
#tabs_outter_conotainer {
 }
#tabs_container {

 }
#user_tools {

 }
divdiv.l-header {
   z-index: 100;
   width:100% !important;
   position:relative !important;
   right:220px !important;
   top:75px !important;
 }
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background:transparent !important; 
   width:100% !important;
 }
div.l-content:before {
   content:url(http://i.imgur.com/TRgfA.png); 
   position:relative; 
   top:-100px; 
   left:-23px !important;
 }
#left_column:after {
   content:url(http://i.imgur.com/zI1pn.png); 
   position:relative; 
   top:0px; 
   left:-14px !important;
 }
#left_column {
   background:url(http://i.imgur.com/8nJs9.png) repeat-y !important; 
   margin-left:-23px !important; 
   padding-left:13px !important;
   width:705px !important;  
   margin-top:-200px !important;
 }
#posts {
   width:552px !important; 
   padding:0px !important;
 }
.avatar_and_i {
   left:-75px !important;
 }         
#right_column {
   background:url(http://i.imgur.com/CN5Mj.png) 8px 0px repeat-y !important; 
   margin: -180px -50px auto -50px !important; 
   width:280px !important;
 }                
#right_column ul, 
#right_column #search_form, 
#right_column .controls_section, 
#right_column .radar {
   width:230px !important; 
   margin-left:32px !important;
 }
#right_column:after {
   content:url(http://i.imgur.com/sOJBq.png) !important; 
   position:relative; 
   top:80px; 
   left:9px !important; 
 }
#right_column li {
   background:transparent !important; 
   opacity:0.7; 
 }
#right_column div.small_links {
   margin-left: 32px !important;}
#right_column .controls_section:first-child {
   margin-left: 32px !important;}
#right_column .controls_section {
   border: 1px solid rgba(255, 255, 255, 0.09) !important;
 }
#right_column a {
   color:white !important; 
   text-shadow: 0px 0px 0px transparent !important; 
   opacity:0.7
 }
#right_column li.selected {
   opacity:1.0; background:url(http://i.imgur.com/gz1DM.png) !important; 
   background-size: 100% 100%  !important;
 }
#right_column li:hover {
   opacity:0.9; 
   background: rgba(255, 255, 255, 0.1) !important;
 }
#right_column li.selected:hover {
   background:url(http://i.imgur.com/gz1DM.png) !important; 
   background-size: 100% 100%  !important;
 }
#right_column li.forever_alone {
   opacity:1.0; 
   background:url(http://i.imgur.com/gz1DM.png) !important; 
   background-size: 100% 100%  !important;
 }
.selection_nipple.white, 
.selection_nipple:not([class="selection_nipple white"]) {
   background: none !important;
   width: 20px !important;
   height: 0px !important;
   margin-top: 0px !important;
   border-bottom: solid 1px white !important;
   border-right: 11px solid transparent !important;
   border-left: 11px solid transparent !important;
   margin-top:-15px !important; 
   padding-top:0px !important;
 }
IMG[src*='http://assets.tumblr.com/images/content_top_edit_form.png'],
IMG[src*='http://assets.tumblr.com/images/content_bottom_edit_form.png']{
   display:none !important;
 }
.white_pane {
   width:600px !important; 
   background:transparent !important; 
   border: 0px !important; 
   box-shadow:none !important;
 }
.notification {
   border:1px #110508 solid !important; 
   background: #24090e !important;
 }
.notification a{
   border:0px !important; 
   text-decoration:underline !important;
 }
.notification a:hover{
   color:white !important; 
 }
.notification blockquote {
   border-left:2px white solid  !important;
 }
.nipple { 
   margin-left:1px !important;
   background: none !important;
   width: 0px !important;
   height: 0px !important;
   border-right: solid 6px rgba(36,9,14, 1.00) !important;
   border-bottom: 7px solid transparent !important;
   border-left: 0px solid transparent !important;
   border-top: 7px solid transparent !important;
   margin-top: 0px !important;
 }
.permalink {
   background: none !important;
   width: 16px !important;
   height: 16px !important;
   background: rgba(170,179,188, .85) !important;
   border-bottom-left-radius: 10px !important;
   border-top-right-radius: 10px !important;
   margin-top: 3px !important;
   margin-right: 4px !important;
}

.permalink:hover {
   background: rgba(127,139,152, 1.0 ) !important;
   box-shadow: -2px 0px 2px #b8c2cc !important;
}
.mceContentBody {
   background:transparent !important;
}
 }

@-moz-document url("http://www.tumblr.com/help"), 
url("http://www.tumblr.com/preferences"),
url-prefix("http://www.tumblr.com/new"),
url-prefix("http://www.tumblr.com/explore"),
url-prefix("http://www.tumblr.com/following"),
url("http://tumblr.com/help"), 
url("http://tumblr.com/preferences"),
url-prefix("http://tumblr.com/new"),
url-prefix("http://tumblr.com/explore"),
url-prefix("http://tumblr.com/following"),
url-prefix("http://tumblr.com/reblog"),
url-prefix("http://www.tumblr.com/reblog")

{
body {
   background: url(http://i.imgur.com/E6Ulg.jpg) center left no-repeat fixed!important;
 }
#logo {
   width:0px !important; 
   height:0px !important; 
   padding-left:210px !important; 
   padding-top:50px !important; 
   background-image:url(http://i.imgur.com/vn53A.png) !important;
 }
.mceContentBody {
   background:transparent !important;
 }
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard/iframe"), 
url("http://www.tumblr.com/upload/image?from_assets"),
url-prefix("http://tumblr.com/dashboard/iframe"), 
url("http://tumblr.com/upload/image?from_assets") {
body {
   background:transparent !important
 }
}
