css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
domain("readmanga.me"), domain("adultmanga.ru"), 
domain("mintmanga.com"), 
domain("readmanga.ru"), domain("doramatv.ru"), 
domain("librebook.ru"), domain("selfmanga.ru"), domain("grouple.co"),
domain("findanime.ru"), domain("grouple.ru"), domain("findanime.me") {
	#mangaPicture {
		opacity: 0.7 !important;
		border-radius: 20px  !important;
	}
	.top_ba, .pull-left {
		display: none !important;
	}
  h1, h2, h3 {
      color: hsl(70, 10%, 70%) !important;
  }
/* 	main text */
	body, div, #menu, menu,  label, td, p, tr, span, .manga-description span, .manga-description p, .manga-description {
		background: hsl(70, 8%, 15%) !important;
		color: hsl(70, 10%, 70%) !important;
		border: hsl(70, 8%, 30%) !important;
		border-radius: 0.5em  !important;
	}
/* 	conteiners */
	.pageBlock, .nav-main-menu,.rightContent {
		border: hsl(70, 8%, 30%) solid !important;
		border-radius: 0.5em  !important;
	}
	.nav-main-menu *, .nav-sub-menu *,.rightContent * {
		background: hsl(70, 8%, 20%) !important;
	}
	
	td, .open-border {
		border-bottom: hsla(70, 30%, 30%, 0.5) 1px solid !important;
	}

/* 	controls, selected */
	th, input, select, option, .button, button, .btn, .form-control, .open , 
	.table-hover > tbody > tr:hover{
		background: hsla(70, 30%, 30%, 0.7) !important;
		color: hsl(70, 10%, 70%) !important;
		border: hsl(70, 30%, 30%) 1px solid !important;
	}
	a:link, a:active, a {
		color: hsl(70, 100%, 50%) !important;
		border: hsl(70, 30%, 100%) !important;
		border-radius: 0.5em;
	}
	a:visited {
		color: hsl(70, 0%, 50%) !important;
	}
	a:hover {
		background: hsla(70, 50%, 50%, 0.2) !important;
		color: hsl(70, 100%, 70%) !important;
	}

/* 	filters */
	#searchform .property .js-link, #searchform span{background-image: radial-gradient(circle at 6px 50%, hsl(70, 10%, 70%), hsl(70, 10%, 70%) 7px, transparent 0%) !important; }
	#searchform .property .js-link.exclude, #searchform span.exclude{background-image: radial-gradient(circle at 6px 50%, red, red 7px, transparent 0%) !important; } 
	#searchform .property .js-link.include, #searchform span.include{background-image: radial-gradient(circle at 6px 50%, green, green 7px, transparent 0%) !important; } 
}
