css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("desmotivaciones.es") {

html, body {
    background: #E4E1D0 !important;
    color: #333333 !important;
    font-family: 'Lucida Grande','Lucida Sans Unicode',Verdana,Arial,sans-serif !important;
    font-size: 13px !important;
}

div.aboveComment {
    background: #E4E1D0 !important;
}

.avatar {
    border: 2px solid #E4E1D0 !important;
}

a:hover {
    color: #ff3333 !important;
}

div.demot {
    -moz-border-radius: 10px;
    border-radius: 10px;
    background: #FFFFFF !important;
    border: 0 !important;
}

.widget_social_facebook1 {
    visibility: hidden;
}

div.main {
    width: 850px !important;
}

.top {
   top: 10px !important;
   -moz-border-radius: 15px;
   border-radius: 15px;
}

.top_links {
    background: #FFFFFF !important;
    -moz-border-radius-bottomright: 15px;
    -moz-border-radius-bottomleft: 15px;
    border-bottom-right-radius: 15px;
    border-bottom-left-radius: 15px;
}

.top, .top_links {    
    margin: 0 auto !important;
    padding-bottom: 0 !important;
    color: #fff !important;
}

#comments img {
    border: 2px solid #212121 !important;
    -moz-border-radius: 10px;
    border-radius: 10px;
}

.comment-mine div {
    color:#ddd;
    padding:5px;
}

.page {
    -moz-border-radius: 10px;
    border-radius: 10px;
    margin: 2px !important;
    padding: 8px 10px !important;
    background: #FFFFFF !important;
    border: 0 !important;
}

.page_active {
    color: black !important;
}

#content-slider {
    background: none !important;
}

.myComment {
    background: none repeat scroll 0 0 #E4E1D0 !important;
}

.newComment {
    background: none repeat scroll 0 0 #fff3ac!important;
}

li.dropdown {
background: none repeat scroll 0 0 white !important;
}

}


@-moz-document url-prefix("http://desmotivaciones.es/editprofile"), url-prefix("http://www.desmotivaciones.es/editprofile"){

.title {
    color: black !important;
    text-align: center !important;
    position: relative;
    top: 20px;
}

div.demot {
    position: relative;
    top: 23px;
}

}

@-moz-document url-prefix("http://desmotivaciones.es/cola"), url-prefix("http://www.desmotivaciones.es/cola"){

.title {
    color: black !important;
    text-align: center !important;
    position: relative;
    top: 20px;
}

.search {
    position: relative;
    top: -254px;
    float: right !important;
    left: -425px;
}

}

@-moz-document url-prefix("http://desmotivaciones.es/top10"), url-prefix("http://www.desmotivaciones.es/top10"){

.title {
    color: black !important;
    text-align: center !important;
    position: relative;
    top: 20px;
}

div.demot {
    position: relative;
    top: 23px;
}

}

@-moz-document url-prefix("http://desmotivaciones.es/sube"), url-prefix("http://www.desmotivaciones.es/sube"){

.title {
    color: black !important;
    text-align: center !important;
    position: relative;
    top: 20px;
}

.error, .alert {
    color: black !important;
    background: white;
    border: 2px #f44 solid !important;
    position: relative;
    top: 23px;
}

div.demot {
    position: relative;
    top: 23px;
}

}

@-moz-document url-prefix("http://desmotivaciones.es/principal"), url-prefix("http://www.desmotivaciones.es/principal"), url("http://desmotivaciones.es/"), url("http://www.desmotivaciones.es/"), url-prefix("http://desmotivaciones.es/buscar"), url-prefix("http://www.desmotivaciones.es/buscar"){

div.main {
    margin-top: 0px !important;
}

}

@-moz-document url-prefix("http://desmotivaciones.es/u"), url-prefix("http://www.desmotivaciones.es/u"){

div.main {
    margin-top: 20px !important;
}

div.demot {
    -moz-border-radius-topleft: 0;
    border-top-left-radius: 0;
}

.float-left {
    padding-right: 5px;
}

ul.tabbernav {
    width: 620px !important;
    margin:auto !important;
}

ul.tabbernav li a {
    background: none repeat scroll 0 0 #eee !important;
    border-bottom: 2px solid #eee !important;
    border: 0 !important;
    color: gray !important;
}

ul.tabbernav li.tabberactive a {
    background: none repeat scroll 0 0 #fff !important;
    border-bottom: 2px solid #fff !important;
    border: 0 !important;

}

.leftItem {
    padding-left: 20px !important;
}

.leftActive {
    background: none repeat scroll 0 0 #E4E1D0 !important;
    color: #333333 !important;
}

.white {
    color: #333333 !important;
}

.pmHover:hover {
    background: none repeat scroll 0 0 #E4E1D0 !important;
}

}

@-moz-document url-prefix("http://desmotivaciones.es/demots"), url-prefix("http://www.desmotivaciones.es/demots"){

body, html {
    background: white !important;
}

}
