css:
@-moz-document regexp("^(https?://)?(zerkalo-|alt.)?rutor.org.*"), regexp("^(https?://)?(zerkalo-|alt.)?tracker.rutor.org.*"), regexp("^(https?://)?(zerkalo-|alt.)?rutor.info.*"), regexp("^(https?://)?(zerkalo-|alt.)?tracker.rutor.info.*"), regexp("^(https?://)?(ru-)?(free-|open-|freedom-|film-|ru)tor\\.(org|info|is).*") {
a[href*="/mail.php"],
a[href*="magnet:"],
a[href="/categories"],
a[href="/upload.php"],
a[href="/jabber.php"],
a[href="/latest_comments"],
tr.backgr,
#logo_mediaget_box,
a[href="#up"],
#mediaget_box,
#download br,
#download img,
#download a.d_small,
strong div[style="text-align: center;"],
a#toolbar,
a.rss,
#down,
td a.downgif,
#up,
#menu_right_side,
#sidebar,
div[onmouseup],
img[src*="s.rutor.org/t/"]
{
    display: none!important
}

div#ws div#content
{
    left: 12px!important;
    right: 12px!important;
}

#displayfiles table
{
    width: 100%!important;
}

#displayfiles table td
{
    padding: 6px!important;
}

#all h1
{
    display: block;
    padding: 4px 20px 0 20px!important;
}

#download a
{
    display: block;
    padding: 12px!important;
}

h2 a:first-child
{
    display: block!important;
    padding: 6px 6px!important;
    border-top: 3px solid #ddd!important;
    margin-bottom: -14px!important;
}

tr.gai td a,
tr.tum td a
{
    display: block;
    padding: 6px 24px!important;
}

tr.gai td:first-child,
tr.tum td:first-child
{
    text-align: center!important;
    color: #ccc!important;
    width: 70px!important;
}

tr.gai:hover td:first-child,
tr.tum:hover td:first-child
{
    color: #333!important;
}

tr.tum
{
    background: #fff!important;
}

tr.gai
{
    background: #eee !important;
}

#index b > a
{
    display: inline-block;
    padding: 6px;
}
}
