css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tripadvisor.com")
{
  /* remove junk */
  .commerce,
  .newsltr,
  .deals
  {
    display: none !important;
  }

  #PAGE
  {
     width: 85% !important;
  }

  #BODYCON
  {
     width: auto !important;
     margin-right: 14px !important;
  }

  .gridA .col.balance
  {
     width: 70% !important;
  }

  .traveler .review .summary
  {
     width: 100% !important;
     margin-right: 94px !important;
  }

  .review .note
  {
     display: none;
  }
}
