css:
@namespace url(http://www.w3.org/1999/xhtml)!important;

@-moz-document domain("www.rememberthemilk.com") {
body {
    background: none!important;
    font-family: Arial!important;
    font-size: 12px!important;
}

#content {
    width: 90%!important;
}

#listbox {
    width: 75%!important;
}



#topnav span, #topnav a {
    font-weight: normal!important;
    text-decoration: none!important;
}

#topnav a, #topnav span span {
    padding: 5px!important;
}

#topnav a:hover {
    text-decoration: underline!important;
}



#searchboxwrap input {
    font-family: Arial!important;
    font-size: 13px!important;
}



#overviewleft {
    width: 75%!important;
}

#overviewright {
    width: 20%!important;
}



.xtabs {
    background: none!important;
}

.xtabs ul {
    padding: 0!important;
}

.xtabs li, .xtabs li.xtab_smartlist {
    float: left!important;
    background-color: #EEE!important;
    background-image: -webkit-linear-gradient(#FFF, #EEE)!important;
	background-image: -moz-linear-gradient(#FFF, #EEE)!important;
    margin-right: 5px!important;
    padding: 0 2px 0 9px!important;
    line-height: 1.8em!important;
    text-decoration: none!important;
    box-shadow: 0 1px 1px #666!important;
}

.xtabs li a:link, .xtabs li a:visited, .xtabs li a:active {
    color: #666!important;
}

.xtabs li.xtab_selected, .xtabs li.xtab_selected a {
    background-image: -webkit-linear-gradient(#0060BF, #004466)!important;
	background-image: -moz-linear-gradient(#0060BF, #004466)!important;
    color: #FFF!important;
    background-position: 0 0!important;
}

.xtabs a, .xtabs li.xtab_smartlist a {
    background: none!important;
    color: #666!important;
    font-weight: normal!important;
}

.xtabs li.xtab_smartlist a:before {
    content: '! '!important;
}



#listtabs {
    width: 20%!important;
    float: left!important;
}

#listtabs ul {
    padding-right: 5px!important;
    margin-top: 155px!important;
}

#listtabs ul:before {
    content: 'Lists:'!important;
    font-size: 20px!important;
    font-family: arial!important;
    color: #CCC!important;
    padding-bottom: 10px!important;
    display: block!important;
}

#listtabs li {
    float: none!important;
    position: relative!important;
    padding: 0!important;
}

#listtabs li a {
    padding: 0 2px 0 9px!important;
    white-space: nowrap!important;
    overflow: hidden!important;
    text-overflow: ellipsis!important;
}

#listtabs .xtab_selected, #listtabs .xtab_selected a {
    background: #046!important;
}

#listtabs .xtab_selected:after {
    content: ''!important;
    width: 0!important;
    border-top: 11px solid transparent!important;
    border-left: 5px solid #046!important;
    border-bottom: 11px solid transparent!important;
    display: block!important;
    position: absolute!important;
    top: 0!important;
    right: -5px!important;
}

#tools_spacer, #tools {
    border: none!important;
}

#listwrap {
    float: left!important;
    width: 79%!important;
}



.xtoolbox_selector {
    font-size: 11px!important;
    margin-top: 10px!important;
}

.xtoolbox_selector a {
    text-decoration: none!important;
    border-bottom: 1px solid #FFF!important;
}



.add-box {
    border: none!important;
    padding-top: 0!important;
}

.add-box .ab1 {
    background: none!important;
}

#add-box .ab6, #add-box .ab7, #add-box .ab8, #add-box .ab9 {
    display: none!important;
}

#add-text {
    font-size: 15px!important;
    padding: 10px 5px!important;
    border-bottom: 1px solid rgba(0,0,0,0.1)!important;
    box-shadow: inset 0px 1px 2px #333!important;
    color: #333!important;
    font-family: Arial!important;
    margin: 0!important;
    background: #FFF!important;
}

#add-text:hover, #add-text:focus {
    background: #E0ECF2!important;
}

#add-label {
    font-size: 11px!important;
    color: #666!important;
    text-transform: uppercase!important;
    margin-left: 10px!important;
}

#add-helpicon {
    content: ""!important;
    width: 16px!important;
    height: 16px!important;
    display: block!important;
    background: url(http://static.zonaprop.com.ar/static/images/icons/help16x16.png) no-repeat!important;
    margin-top: 5px!important;
}

#midcontent > div {
    border: none!important;
}

.xtable {
    border-collapse: separate!important;
    border: none!important;
    background: none!important;
}



.xtr td {
    background-image: -webkit-linear-gradient(#EEE,#E8E8E8)!important;
	background-image: -moz-linear-gradient(#EEE,#E8E8E8)!important;
    border-top: 1px solid #FFF!important;
    border-bottom: 1px solid #CCC!important;
}

.xtr_keyboard .xtd, .xtr_keyboard .xtd_arr, .xtr_keyboard .xtd_arr:after {
    
}

.xtd_arr {
    background: none!important;
}

.xtr_keyboard .xtd_arr:after {
    content: 'Â»'!important;
    margin-left: 5px!important;
}

.xtr.xtr_hover td, .xtr.xtr_hover .xtd_arrj {
    background-image: -webkit-linear-gradient(#DDD,#CCC)!important;
	background-image: -moz-linear-gradient(#DDD,#CCC)!important;
}

.xtr.xtr_select .xtd {
    background-image: -webkit-linear-gradient(#E0ECF2,#BFDCEB)!important;
	background-image: -moz-linear-gradient(#E0ECF2,#BFDCEB)!important;
}



.xtd {
    border-top: 1px solid #CACACA!important;
    height: 40px!important;
    font-size: 14px!important;
    text-shadow: 1px 1px 0 rgba(255,255,255,0.75)!important;
}



.xtd_date {
    font-variant: small-caps!important;
}



.xtd_tag {
    font-variant: small-caps!important;
    color: #999!important;
    margin-left: 10px!important;
}

.xtd .taskoverdue {
    text-decoration: none!important;
    color: red!important;
    text-shadow: 1px 1px 0 rgba(255,255,255,0.5)!important;
}

.xtd_ico {
    border-right: 1px solid #CCC!important;
}



.col_prio {
    width: 10px!important;
}

.xtr .prioN {
    border-left: 1px solid #CCC!important;
    border-top: 1px solid #FFF!important;
    border-bottom: 1px solid #CCC!important;
}

.xtr .prio1, .xtr.xtr_hover .prio1, .xtr.xtr_select .prio1 {
    background-image: -webkit-linear-gradient(#FF0000, #990000)!important;
	background-image: -moz-linear-gradient(#FF0000, #990000)!important;
}

.xtr .prio2, .xtr.xtr_hover .prio2, .xtr.xtr_select .prio2 {
    background-image: -webkit-linear-gradient(#0060BF, #004466)!important;
    background-image: -moz-linear-gradient(#0060BF, #004466)!important;
}

.xtr .prio3, .xtr.xtr_hover .prio3, .xtr.xtr_select .prio3 {
    background-image: -webkit-linear-gradient(#359AFF, #004A95)!important;
    background-image: -moz-linear-gradient(#359AFF, #004A95)!important;
}



#detailsbox {
    border: none!important;
    width: 25%!important;
}

#detailswrap {
    width: auto!important;
}

#details {
    background: #EEE!important;
    border-radius: 2px!important;
    border: 1px solid #CCC!important;
    width: auto!important;
}

#detailstabs, #tasktabs {
    margin: 15px!important;
    margin-bottom: 0!important;
}

#detailstitle {
    font-size: 16px!important;
    padding-left: 5px!important;
    width: auto!important;
}

#detailstitle input {
    font-family: Arial!important;
}

#taskdetails div {
    border-bottom: 1px solid #DDD!important;
    font-size: 11px!important;    
}

#taskdetails div span {
    display: block!important;
    padding-left: 5px!important;
    line-height: 20px!important;
}

#taskdetails div span:hover {
    background:none!important;
}

#taskdetails div span .field {
    display: inline!important;
}

#taskdetails div span .field a {
    display: inline-block!important;
}

#listtoolbox {
    width: auto!important;
}

#listtoolbox a {
    font-weight: normal!important;
    font-size: 9px!important;
}

#taskcloud_copy, #taskcloud_copy .taskcloudbox, #keywrap {
    width: auto!important;
}

#smartadd-cheat {
    box-shadow: 0 1px 4px #000!important;
    border: none!important;
    border-radius: 5px!important;
}

.appfooter {
    clear: both!important;
}



.xtoolbox_button {
    font-size: 11px!important;
    font-family: Arial!important;
    border: 1px solid #DDD!important;
    background-color: #EEE!important;
    background-image: -webkit-linear-gradient(#EEE,#CCC)!important;
	background-image: -moz-linear-gradient(#EEE,#CCC)!important;
    padding: 2px 10px!important;
    line-height: 1.8em!important;
    text-decoration: none!important;
    border-radius: 3px!important;
    box-shadow: 0 1px 1px #999!important;
    color: #666!important;
    text-shadow: 1px 1px 0 #FFF!important;
}

.xtoolbox_button:hover {
    cursor: pointer!important;
    background-image: -webkit-linear-gradient(#FFF,#DDD)!important;
	background-image: -moz-linear-gradient(#FFF,#DDD)!important;
}



select {
    font-family: sans-serif!important;
    font-size: 11px!important;
}

#list {
    margin-right: 5px!important;
    overflow: hidden!important;
    width: 100%!important;
}



#statusbox {
    position: absolute!important;
    left: 20%!important;
    right: 30%!important;
    width: auto!important;
}

#statusboxtext {
    font-family: Arial!important;
    font-weight: normal!important;
    font-size: 14px!important;
    text-shadow: 0 1px 0 rgba(255,255,255,0.5)!important;
}

.orange_rbtop, .orange_rbbot {
    display: none!important;
}

.orange_rbroundbox {
    background: rgba(255, 160, 0, 0.5)!important;
    padding: 10px 10px 10px 0!important;
    box-shadow: 0 1px 4px rgba(0,0,0,0.5)!important;
    border-radius: 5px!important;
}

/* KEYBOARD SHORTCUTS */
/* Based on: http://userstyles.org/styles/1623/remember-the-milk-display-keyboard-shortcuts */

#detailstitle_highlight[title]:after {
    content: "Rename [r]";
    color: #888;
    font-weight: normal;
    font-family:monospace;
    font-size:75%;
}

#detailsdue         .field:after {content: " [d]"; font-family:monospace; color:#888;}
#detailsreoccur     .field:after {content: " [f]"; font-family:monospace; color:#888;}
#detailsduration    .field:after {content: " [g]"; font-family:monospace; color:#888;}
#detailstags        .field:after {content: " [s]"; font-family:monospace; color:#888;}
#detailslocation    .field:after {content: " [l]"; font-family:monospace; color:#888;}
#detailsurl         .field:after {content: " [u]"; font-family:monospace; color:#888;}
#detailspostponed   .field:after {content: " [p]"; font-family:monospace; color:#888;}
#detailsnotes       .field:after {content: " [y]"; font-family:monospace; color:#888;}

#tasksToolbox > .xtoolbox_selector > a:first-child::after { content: "[a]"; font-family:monospace;}
#tasksToolbox > .xtoolbox_selector > a:last-child::after  { content: "[n]"; font-family:monospace;}

#add-label:after { content: " [t]"; font-family:monospace; color:#888;}

#tasktabs:after, #detailstabs::after {
    content: "[h]";
    font-family:monospace;
    color:#888;
}

#searchtogglewrap::before {
    content: "[C+S+/] ";    
}

#viewSelector > a[href="#section.overview"]::after      {content: "[C+S+6]";}
#viewSelector > a[href="#section.tasks"]::after         {content: "[C+S+7]";}
#viewSelector > a[href="#section.locations#"]::after    {content: "[C+S+8]";}
#viewSelector > a[href="#section.contacts"]::after      {content: "[C+S+9]";}
#viewSelector > a[href="#section.settings"]::after      {content: "[C+S+0]";}

#viewSelector > a::after {font-family:monospace;}

#tasks:before {
    content: "List shortcuts: Up[k] Down[j] Select[i] Complete[c] Pospone[p] Undo[z] Multi-edit[m]";
    margin: 0 0 10px 20px;
    padding: 5px 10px;
    display: inline-block;
    background: #EEE;
    font-family:monospace;
    color:#888;
    border-radius: 3px;
    text-shadow: 0 1px 0 #FFF;
}
}
