css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/***STANDARD WEB STUFF***/
html,body,head,foot{background:#000!important;color:#fff!important;}
a,a:visited{color:#fd9!important;border-color:#229!important;}
b{color:#fff!important;}
hr{border-color:#229!important;}
input{background-color:#000!important;color:#fff!important;}
textarea,select
{
-moz-appearance: none !important;
color:#fd9!important;
border-color:#333!important;
background-color:#000!important;
}

/***TOP BLUE BAR***/
#blueBar{background:#005!important;}

/***SEARCH***/
.uiSearchInput {background-color:#000!important;border-color:#229!important;}
.search{background:#002!important;}
.page,.calltoaction,.header,.user:not(.selected){background:#002!important;border-color:#115!important;}

/***NAVIGATION MENU***/
#pageNav ul{background:#002!important;}
.menuDivider{background:#22a!important;}
.menuPulldown{background-color:#005!important;}

/***MAIN CONTENT***/
#contentCol{background:#000!important;color:#fff!important;border-color:#115!important;}
#contentArea li{border-color:#115!important;}
#contentArea{background:#002!important;}
.uiStreamHeaderBorder,.uiStreamHeader,.uiStreamHeaderChronologicalForm{background:#115!important;}
.clearfix span{color:#fff!important;}
.mts{color:#fff!important;}
/*Buttons*/
.uiButton{background:#004!important;}
/*UI List?*/
.uiListLight{background:transparent!important;}
.uiStreamHeaderText{background:transparent!important;}

/*New Shit*/
.fbTimelineSection div{background:#002!important;border-color:#555!important;}
.rhc{background:rgba(0,0,50,.5)!important;}
.uiContextualLayer div{background:rgba(0,0,50,.7)!important;}

/*Add Photo-Video-Question*/
.borderItem,.uiComposerMetaContainer{background:#005!important;border-color:#229!important;}
.title{color:#fff!important;}
.title:hover{background:#229!important;}

/*What's On Your Mind*/
.uiComposer{border-color:#229!important;}
.inputContainer{background-color:#005!important;border-color:#229!important;}
.uiComposerMetaContainer{border-color:#229!important;}
#pagelet_composer div{background-color:transparent!important;border-color:#229!important;}

/*New Content Banner*/
.uiBoxLightblue{background:#002!important;border-color:#115!important;}

/*Content Comments*/
.UFIList li,.uiUfiAddComment,.ufiItem,.uiUfiComments li{background:#001!important;border-color:#229!important;}
.uiTypeahead {background:#002!important;border-color:#229!important;}
[class="uiTypeahead mentionsTypeahead"] .wrap{border-color:#229!important;}

/*The like-share menu*/
.uiUfiLike{background:#001!important;}

/*Chat*/
.fbNubFlyoutTitlebar{background:#005!important;}
.fbNubButton{background:#001!important;border-color:#229!important;}
.fbChatSidebarBody{background:#050505!important;}
.uiMenuSeparator,.uiMenu{background:#001!important;border-color:#229!important;}
.fbNubFlyoutBody{background:#001!important;border-color:#115!important;}
a.clearfix:hover{background:#115!important;}
/*this changes the chat count at the bottom right*/
.count strong{color:#aaf!important;}
.conversation,.fbChatConvItem{background:#001!important;border-color:#115!important;}
.titlebar{background:#005!important;}
.fbChatSidebarMessage{background:#002!important;border-color:#115!important;}


/*Hovercards*/
.stage,.HovercardContent{background:#001!important;}
.HovercardContent span{color:#fff!important;}
.HovercardWithFooter,.uiBoxGray{background:#002!important;border-color:#229!important;}

/***LEFT SIDE MENU***/
.navHeader{background:#005!important;color:#fff!important;}
#leftCol [class="item clearfix"]{background:#000!important;}
.uiSideNavSection a:hover{background:#009!important;}
.bookmarksNavSeeAll{color:#fd9!important;}
.uiHeader{background:#005!important;border-color:#229!important;}

/***RIGHT SIDE STUFF***/
.fbFeedTickerStory{background:#002!important;border-color:#115!important;}
.rightColumnWrapper div{border-color:#115!important;}
.UIImageBlock_Content h6{color:#fff!important;}
.tickerFeedMessage .passiveName{color:#fd9!important;}
.tickerFeedMessage .token{color:#fff!important;}
#mainContainer{border-color:#229!important;}
.fbSidebarGripper div{background:#005!important;border-color:#229!important;}

/*Should go under chat, but maybe not*/
.inner span{background:#004!important;border-color:#229!important;}

/*When you click on something in the newsflash window, it pops up a window*/
.tickerDialogFooter,.uiOverlayContent{background:#001!important;border-color:#115!important;}

/*UI Buttons*/
.uiButton{background-color:#115!important;border-color:#229!important;}
.profileHeaderButton *,.uiSelectorButton {background-color:#115!important;border-color:#229!important;}

/*People You May Know, etc*/
.uiSideHeader{background:#005!important;}
h4{color:#fff!important;}
.ego_unit{border-color:#229!important;}

/***PHOTOS***/
.uiMediaThumb{background:#005!important;}
.fbxPhoto,.overlay{background:rgba(0,0,32,.5)!important;}
.container{background:#002!important;}
.vTop,vBot{background:#000!important;border-color:#229!important;}
.photoWrapper a{background:#002!important;}
#fbPhotoSnowboxActions{border-color:#115!important;}
.photoWrapper a i{background-color:#002!important;}

/*Adding A Photo*/
.grayArea{background:#001!important;border-color:#229!important;}
.mvl hr{background:#229!important;}
.overlap{background:#005!important;border-color:#229!important;}

/***USER INFORMATION***/
#contentCurve,#footerContainer{background:#001!important;border-color:#229!important;}

/***MESSAGING SYSTEM***/
.MessagingReadHeader{background:#005!important;}
.dialog_content div{background:#004!important;border-color:#229!important;}

/*SURVEY*/
.qsurvey{background:#001!important;color:#fff!important;border-color:#229!important;}
.pbs{color:#fff!important;}

/***NOTIFICATIONS***/
.fbJewelFlyout,.fbJewelFlyout li{background:#001!important;border-color:#229!important;}
.seeMore{background:#001!important;}
.uiHeaderTitle{color:#fff!important}

/***FRIEND SPAM***/
.megaphone_box{background:#001!important;border-color:#229!important;}
hr{background:#229!important;}

/***EVENTS***/
.fbCurrentThickline{background:#229!important;}

/***POP-UP NOTIFICATIONS***/
.UIBeep,.UIBeeper_Full{background:#004!important;color:#fff!important;border-color:#229!important;}
.pop_container_advanced .text{color:#fff!important;}

/***Profile Shit***/
.uiTokenizer{background:#000!important;border-color:#229!important;}
}
