css:
/* 
 * @name 		Google Calendar Minimizer
 * @website		http://userstyles.org/styles/51875
 * @description	Optimize Google Calendar for smaller Screens by moving some Elements
 * @version		1.1
 * @date 		2016-05-13
 */

@-moz-document 
  url-prefix('https://calendar.google.com/calendar/render'), 
  url-prefix('https://www.google.com/calendar/render'), 
  url-prefix('http://www.google.com/calendar/render') {
    #gridcontainer{
        margin-right: 0px !important;
    }
    #nav{
        margin-left: 17px !important;
    }
    #vr-nav {
        margin-top: 4px !important;
        padding-bottom: 3px !important;
    }
    #mainbody {
        padding-top: 1px !important;
        margin-left: 175px !important;
    }
    #dp_0.dpdiv, .calHeader, #lhscalinner_my {
        padding: 0;
    }
    .calListRow {
        height: 25px;
    }
}
