css:
#navigator-toolbox[tabsontop="true"] #nav-bar {padding: 4px 2px !important;border-radius: 3px 3px 0 0 !important;}
#navigator-toolbox[tabsontop="true"][iconsize="small"] #nav-bar {padding: 3px 2px !important;}

#urlbar {
  height: 24px !important;
}

.autocomplete-history-dropmarker.urlbar-history-dropmarker{display:none !important}

#star-button {
visibility: visible !important;
}

#identity-box {
visibility: visible !important;
}

#searchbar {
  height: 26px !important;
}

#searchbar .searchbar-dropmarker-image {
display: none !important;
}

.tabbrowser-tab .tab-stack {
  height: 25px !important;
  
}

@-moz-document url-prefix('chrome://') {

.tabbrowser-tab:not([pinned])[fadein]
{
  max-width: 200px !important;
  min-width: 100px !important;
}

}

#navigator-toolbox[tabsontop=true] #nav-bar {
box-shadow: 0 1px 0 0 #f7f7f7 inset,
0 -1px 0 0 rgba(0,0,0,.05) !important;
background-image: -moz-linear-gradient(#f5f5f5,#e9e9e9) !important;
}
#navigator-toolbox[tabsontop=true] > toolbar:not(#toolbar-menubar):not(#TabsToolbar) {
background-color: #e9e9e9 !important;
}
.tabbrowser-tab[selected="true"] .tab-stack {
-moz-border-image: url('chrome://browser/skin/tabbrowser/mrcurve-grey.png') 0 28 0 28 / 0 28px 0 28px !important;
}

#go-button { display: none !important }
#urlbar > dropmarker { display: none !important; }
#urlbar {-moz-border-radius-topleft: 3px !important;
		-moz-border-radius-topright:3px !important;
-moz-border-radius-bottomleft:3px !important;
-moz-border-radius-bottomright:3px !important;}
