css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.toodledo.com/tasks/index.php'), url-prefix('https://www.toodledo.com/tasks/index.php') {

#userstylewarning { display: none !important; }

.row:hover, .row .dett:hover {background-color:#EDF7FF !important;}
.subtasks .row:hover,.subtasks  .row .dett:hover {background-color:#DBF0FF !important;}

.subtasks:hover {background-color:#EDF7FF !important;}

.pri3, .pri3 span { color:#C00000 !important; }
.pri2, .pri2 span { color:#00179B !important; text-shadow: none !important;}

.date3 { color: #C00000 !important; }
.date2 { color: #217000 !important; }

.status1 { color:#C00000 !important; font-weight:normal !important; text-shadow: none !important;}
.status2 { color: #217000 !important; font-weight:normal !important; text-shadow: none !important;}

.dim { color:transparent !important; background-color:transparent !important; }
.dim:hover { color: #999 !important; background-color:#fff!important; }

.dimish { color: #999 !important; }

.rowcomp,.rowcomp .task,.rowcomp .pri0,.rowcomp .pri1,.rowcomp .pri2,.rowcomp .pri3,.rowcomp .date0,.rowcomp .date1,.rowcomp .date2,.rowcomp .date3 {color:#999 !important; }

body, #blue.chrome body { background:url(http://static.toodledo.com/images/css/banner.png) repeat-x 0px -25px !important; }
#logo { width:120px !important; height: 27px !important; margin-left: 5px !important; margin-top: 10px !important;}
#banner { height: 50px !important; background:#b5caeb url(http://static.toodledo.com/images/css/banner.png) repeat-x 0px -25px !important; }
#welcome { }
.sec_btn { height: 25px !important; background:#b5caeb url(http://static.toodledo.com/images/css/tabs.png) repeat-x 0px -102px !important; }
.sec_btn_sel { height: 26px !important; background:#b5caeb url(http://static.toodledo.com/images/css/tabs.png) repeat-x 0px 0px !important; }
.hov_btn { top: 26px !important; }
#sec_task { left: 125px !important; top: 12px !important; }
#sec_notebook { left: 231px !important; top: 12px !important; }

#main { width: 100% !important; background: url(http://static.toodledo.com/images/css/ribbon.png) repeat-y -103px 0px !important;}

#rchunktop { left: 86px !important; top: 65px !important; padding: 0px 0px 0px 13px !important; position: absolute !important; }
#rchunk { margin: 50px 0px 0px 86px !important; padding: 40px 0px 0px 13px !important; background: #FFFFFF !important; }

#bottom { width: 100% !important;}

#left_side { width: 86px !important; margin: 50px 0px 0px 0px !important; padding: 5px 0px 0px 0px !important; position: absolute !important; background:url(http://static.toodledo.com/images/css/ribbon.png) repeat-y -103px 0px !important;}
#left_side:hover { z-index: 50 !important; box-shadow: 1px 1px 4px rgb(0, 0, 0); width: 200px !important; background: rgb(229,229,224) !important;}


#viewby { width: 100% !important; padding: 0px 1px 5px 1px !important;}
#viewby h3 { font-size: 14px !important; margin: 10px 0px 10px 10px !important; }
#viewby a { font-size: 10px !important; width: 45% !important; margin: 0px 1px 0px 1px !important; }

.tabs { width:100% !important; padding: 10px 5px 10px 5px !important; }
.tab { width: 100% !important; padding: 5px 3px 5px 3px !important; }
.tabon { width: 100% !important; padding: 5px 3px 5px 4px !important; }
#left_side .badge { margin-right: 6px !important; }

}
