@namespace url(http://www.w3.org/1999/xhtml);











@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {







body {



    background: url('http://28.media.tumblr.com/tumblr_lnjfbpQd451qkonooo1_500.png') top left #121212 repeat fixed !important;



  }





#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    background:#FAFAFA !important;



    -webkit-border-radius: 20px;



    -moz-border-radius: 20px;



    border-radius: 20px;



    margin: auto;



    padding: 20px 20px;



  }



#left_column #posts .post .permalink {

background-color:#f5f5f5 !important;

}



#left_column #posts .post .post_avatar,

#left_column #posts .post .sub_avatar .sub_avatar_inner {

background-color: #fbfbfb !important;

border-bottom-color: #eee !important;

}



#left_column #posts .post{

border-bottom:1px #eee solid !important

}



#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#666 !important;

background-image:none !important;

border:0px !important

}



#nav .nav_item .nav_menu {

border-bottom:0px !important

}



#nav .nav_item .nav_menu a {

color:#888 !important;

border-top-color:#666 !important;

}



#nav .nav_item .nav_menu a:hover {

color:#666 !important;

}



#footer, #footer a {

color:#666 !important;

background-color:#fbfbfb !important;

background-image:none !important;

}



.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {

color:#ccc !important

}



#right_column .dashboard_nav_item {

border-bottom:1px solid #666 !important;

background-image:none  !important

}



#right_column .dashboard_nav_item .dashboard_nav_title,

                    #right_column .dashboard_nav_item .dashboard_nav_title a,

                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:#666 !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#888 !important ;

                    }



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        background: none !important;

                    }



#show_radar a, #hide_radar a,

                        #hide_recommended_blogs a, #show_recommended_blogs a {

                            color:#666 !important;

                        }





#left_column #posts .notification {

                        background-color:#fbfbfb !important;

                        color:#666 !important;

                        border-bottom-color: #eee !important

                    }



#left_column #posts .notification .block {

                            color:#333 !important;

                        }



#left_column #posts .notification.alt {

                        background-color: #f7f7f7 !important;

                    }

#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#ececec !important;

                    }

#left_column #posts .notification a {

                        color:#565656 !important;

                        border-bottom:0px !important;

                    }

#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:#FAFAFA !important;

                        background-color:#fbfbfb !important;

opacity:1 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }



.dashboard_switch_blog_menu_header{

background-color:#fbfbfb !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_item{

background-color:#fbfbfb !important;

color:#666 !important;

border:0px !important

}

.dashboard_switch_blog_menu_footer{

background-color:#fbfbfb !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_wrapper{

background-color:#fbfbfb !important;

}



#new_post_notice_container{

color:#fff !important

}



#new_post_notice_container a{

color:#fff !important

}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{

background-image:none !important

}

.notification_type_icon{

display:none !important

}





h1,h2,h3,h4,h5,h6{color:#555555 !important}



a, a:visited{color:#666666 !important}



a:hover{color:#888888 !important}










img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

    width:0;

    height:70px;

    padding-right:250px;

    opacity: 0;

}



ol#posts li.post.new_post {

background: #ffffff url('http://i54.tinypic.com/2qnywk6.png') top center no-repeat !important;

}

.new_post_label {
font-size:14px !important;
padding-top:73px !important;
font-family:trebuchet ms !important;
text-transform: lowercase !important;
color:#222 !important;
letter-spacing:1px !important
}
}
ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_text.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_photo.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_quote.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_link.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_audio.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_video.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_chat.new_post_label span.new_post_label_icon {
background-image: none  !important;
}





#logo {height: 0 !important;







	width: 0 !important;





	padding-left: 230px !important;







	padding-top: 85px !important;









	background: url(http://static.tumblr.com/ettmxkg/CsGlzwdfh/doodle-logo.png) no-repeat !important;}

.sub_avatar{width:32px !important; height:32px !important; background-color:#FAFAFA !important}
