css:
@-moz-document domain('music.google.com'){
#oneGoogleWrapper {
    background: #2d2d2d;
    border-bottom: 1px solid #e5e5e5;
    color: white;
    height: 26px;
    z-index: 100;
    position: relative;
}

#oneGoogleWrapper a, #oneGoogleWrapper a u {
    color: #ccc;
    text-decoration: none;
    padding: 3px 2px;
    font-size: 13px;
}

#oneGoogleWrapper a:hover {
    border-top: 2px solid #d14863;
    color: white;
}

#oneGoogleWrapper div {
    margin-top: 0px;
    padding-top: 5px;
}

.g-section#header {
    height: 0;
}

.g-section#header .logo,.g-section#header .search-border, .g-section#header #topBarContainer {
    display: none;
}

.g-section#header .search {
    position: absolute;
    top: 30px;
    right: 360px;
}

.g-doc {
    position: relative;
    top: -18px;
}

#oneGoogleWrapper #gbar a:nth-child(1):before {
    content: "Music";
    border-top: 2px solid #d14863;
    color: white;
    display: block;
    font-weight: bold;
    padding: 3px 2px 3px 0px;
    margin-right: 11px;
}

#countSummary {
    display: none;
}
}
