css:
@-moz-document domain('timetracker.i4.net'), url-prefix('https://timetracker.i4.net/') {

h1 {color: #9ec023 !important;}


h4 {border-bottom: 1px solid #9ec023 !important;}

img[alt="i4 Solutions"]{
	height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 350px !important;
	padding-top: 38px !important;
	background: url('http://www.launchpadauctions.com/gb/stylish/i4_logo_white.png') no-repeat !important;
}

.btn {
	color: #000 !important;
	background-color: #9ec023 !important;
	border: 1px solid #2a65c1 !important;
}

.btn2 {
	color: #2a65c1 !important;
	border: 1px solid #9ec023 !important;
}

.img2 {border: 1px solid #9ec023 !important;}

.left2 {border: 1px solid #9ec023 !important;}

.right2 {border: 1px solid #9ec023 !important;}

/* Time Tracker Styles */

.siteheader {
	background-color: #9ec023 !important;
	border-bottom: 5px solid #2a65c1 !important;
}

.navbar {color: #9ec023 !important;}

.navbar a:hover{color: #9ec023 !important;}

.colheader, .hdr1 {
	background-color: #9ec023 !important;
	border-bottom: 3px solid #2a65c1 !important;
	-moz-border-radius-topright: .8em;
	-moz-border-radius-topleft: .8em;
}

.progress {background-color: #9ec023 !important;}

.sprogress {background-color: #9ec023 !important;}


.progress1 {background-color: #2a65c1 !important;}

#tabActive{	background-color: #9ec023 !important;}

#wip_list .colheader {
	background-color:#2a65c1 !important;
	border-bottom:3px solid #9ec023 !important;
}


/*****************************************/
/***          DEFAULT STYLES           ***/
/*****************************************/

/* .required { background: url('../images/required.gif') no-repeat left; padding-left: 10px;} */

/*****************************************/
.rowSel{ background-color: #9ec023 !important;}

/*  .rowGrayed{ background: url('images/grayed.gif'); } */

.employeeHours input.hasalert{border-bottom:solid 1px #2a65c1 !important;}

.employeeHours thead tr td{background-color:#2a65c1 !important;}

.employeeHours tbody tr td div.iw{border:solid 1px #2a65c1 !important;}

.employeeHours tr.summary table input{background-color:#2a65c1 !important;}

.tasksTbl .componentRow textarea:focus {height:20em; width:40em;}

/*****************************************/
/****   TASKS                        *****/
/*****************************************/

.prs_1:hover
, .prs_2:hover
, .prs_3:hover
, .prs_4:hover
, .prs_5:hover
, .prs_6:hover
, .prs_7:hover
, .prs_8:hover
, .prs_9:hover
 { background-color:#2a65c1 !important;}
 
}
