css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("oneclickmoviez.com") {

#hid { visibility: visible !important; }

#wp_sidebarlogin-3,
#recent-comments-2,
#footer,
.jump,
.postmetadata,
#comments,
#text-686435915,
#text-686435913,
#text-686435914,
#archives-2,
#calendar-2,
#topbar,
.blogname,
#searchsubmit,
.top,
#toggler
{ display: none !important; }

body, .top, #foxmenucontainer { background: #fff !important; }

* { font-family: "lucida grande",tahoma,verdana,arial,sans-serif; font-size: 12px !important; }

#foxmenucontainer { background: -moz-linear-gradient(center bottom, #4477AA, #336699) !important; position: absolute; right: 0; left: 0; padding-top: 30px !important; }

#foxmenu ul:before { content:"OneClickMoviez"; float:left; width:180px; height:20px; margin-top:-4px; color:#fff; font-size:26px; font-weight:100; font-family:"Helvetica Neue"; opacity:1; text-shadow: #fff 0 0 3px, #000 0 1px 0; } 


#foxmenu { width: 1000px; margin: 0 auto; padding-left: 28px; }
#foxmenu ul, #foxmenu ul li { float: left !important; width:auto !important; padding-left: 5px; }
#foxmenu ul li a { background: rgba(255,255,255,0.1) !important; padding: 7px 0 3px 0 !important; color:#fff !important; -moz-border-radius-topleft: 3px; -moz-border-radius-topright: 3px; font-weight:normal !important; width:100px !important; }
#foxmenu ul li a:hover { background: #fff !important; color:#369 !important; }

.content, #myslides { background: #fff !important;}

.stepcarousel { background: #fff !important; border: 0 !important; height: 180px !important; margin-top: 80px; }
.stepcarousel .panel { height: 180px !important; padding: 10px 1px; }
.stepcarousel .panel img { -moz-box-shadow: 0 0 6px #999; border: 1px solid #fff !important; }
.stepcarousel .panel img:hover { -moz-box-shadow: 0 0 6px #69c; }

.post, .widget { background: #fff !important; border-bottom: 1px solid #eee !important; margin:0 !important;}
h2 { background: rgba(0,0,0,0.02) !important; color: #333 !important; font-size: 14px !important; height: auto !important; padding: 10px !important; line-height: inherit !important; font-size: 12px !important; }
.post h2:focus { background: rgba(0,0,0,0.1) !important; }

.post a, .navigation a { color: #369 !important }
.post *, .post .title a, .widget * { color: #333 !important; }

.title { padding: 0 !important; height: auto !important; background: none !important; }
.date { padding: 10px !important; font-size: 10px !important; float:right; opacity:0.3 !important; font-variant:normal !important;}
.alignnone { float: left !important; }

.post .cover { display:none; background: none !important; min-height: 350px; }
.post:hover .cover { display:block; }

.navigation { margin: 30px; }


.widget ul, .widget li { background: none !important; border: 0 !important;}


input#s { width: 200px; margin-bottom: 20px; }




}
