    padding-left: 600px !important;
}
#group-create ul.group-choice {
    color: gray !important;
}
#Main > #subnav-template-positionmask + h1 {
    position: absolute !important;
    display: inline-block;
    width: 400px !important;
    height: 28px;
    line-height: 24px ;
    left: 5px;
    top: 80px !important;
    text-align: center;
    z-index: 500 !important;
}
#Main table tbody tr td#Hint {
    display: inline-block;
    width: 407px;
    height: 800px !important;
    margin-left: 5px !important;
    margin-top: 5px ;
}
.Problem {
    position: absolute ;
    width: 407px;
    right: 553px ;
    top: 0px ;
    padding: 5px 5px 10px 38px;
    border-radius: 5px ;
background-color: black ;
}
#global-nav.styleguide-global-nav + #Main table tbody tr td#Hint p:only-of-type {
    position: absolute !important;
    display: inline-block;
    width: 370px !important;
    height: auto !important;
    line-height: 18px !important;
    left: -5px !important;
    top: 40px !important;
    margin-bottom: -10px;
    padding: 10px !important;
    font-size: 16px ;
    text-align: left !important;
}
/* == END == URL PREF == CREATE GROUP === */
}

@-moz-document url-prefix("https://www.flickr.com/iconbuilder/?group=") {
/* ===== START == URL PREF == CREATE ICON GROUP === */
#iconbuilder-container {
    height: 1000px;
    width: 800px !important;
    padding-left: 600px ;
}
/* == END == URL PREF == CREATE ICON GROUP === */
}

@-moz-document url("https://www.flickr.com/groups"), url-prefix("https://www.flickr.com/groups?") {
/* == TEST - START == URL / URL PREF  == YOUR GROUP (work but PB strange height) - === */
body.new-footer.new-footer-tight-ass #main {
    display: inline-block !important;
    float: none !important;
    height: auto !important;
    height: 100% !important;
    min-height: 100% !important;
    min-height: 3000px !important;
    max-height: 100% !important;
    overflow: visible !important;
}
/* == END == URL == YOUR GROUP === */
}

@-moz-document url-prefix("https://www.flickr.com/search/forum/") {
/* ===== START == URL PREF - SEARCH  === */
html.styleguide body {
    padding-top: 0 !important;
}
/* == END == URL PREF - SEARCH ==== */
}

@-moz-document url-prefix("https://www.flickr.com/help/forum/") {
/* ==== START == URL PREF == HELP ==== */
html.styleguide {
    display: inline-block !important;
    width: 100% !important;
    height: 100% ;
    min-height: 927px !important;
    max-height: 927px !important;
    top: 0px !important;
    overflow: hidden !important;
    overflow-y: hidden !important;
}
/* (new164) #Main - HELP */ 
#Main {
    position: relative !important;
    display: inline-block !important;
    height: 100% !important;
    min-height: 99.5vh !important;
    max-height: 99.5vh !important;
    min-width: 100% !important;
    top: 0 !important;
    margin-bottom: 0px !important;
    overflow: hidden !important;
    overflow-y: hidden !important;
}
/* (new164) HELP - FORUM + EDIT TOPIC */
#Main > h1 + table  {
    position: relative !important;
    display: inline-block !important;
    height: 100% !important;
    min-height: 99.1vh !important;
    max-height: 99.1vh !important;
    min-width: 100% !important;
border: 1px solid red !important;
}

#Feeds {
    display: none;
}
/* == END == URL PREF == HELP ==== */
}

@-moz-document url-prefix("https://www.flickr.com/mail") {
/* == START == URL PREF == MAIL ==== */
#Main {
    width: 1906px !important;
    top: 0px !important;
}
/* == END == URL PREF == MAIL ==== */

/* STYLE 5 == END == PREFIX ==== */
}

@-moz-document url-prefix("https://www.flickr.com/activity/"), url-prefix("https://www.flickr.com/recent_activity.gne?") {
/* ===== START == (new165) URL PREF - MAIN H1 - ACTI === */
#Main[role="main"]>h1 {
    position: relative ;
    height: 20px ;
    line-height: 20px ;
    width: 130px ;
    left: 1690px ;
    top: 6px ;
    padding: 0 ;
    font-size: 13px ;
    text-align: center ;
border: 1px solid gray ;
}
/* == END == URL PREF - MAIN H1 - ACTI  === */
}

@-moz-document url-prefix("https://www.flickr.com/people/") {
/* ===== START == (new165) URL PREF - ABOUT PHOTO OF === */
#Main > table:not(.PeopleResults):not(#Photo):not(#SubNav):not(.TopicListing):not(.acct-sett):not(#MemberList) {
    top: 100px !important;
}
#global-nav.styleguide-global-nav + #Main #subnav-refresh:not(.groups) ~ table tbody tr td#Hint + #GoodStuff:not(.admin_moderation) {
    width: 1350px !important;
    margin: 0px !important;
    right: 20px;
    top: 0px;
    padding: 20px;
    border-radius: 5px ;
border: 1px solid gray ;
/* background: red !important; */
}
#Main table tbody tr td#Hint {
    position: sticky;
    display: inline-block;
    height: auto;
    width: 440px;
    margin-top: 0px;
    padding-top: 5px;
/* background: blue !important; */
}
.ThinSeparated.photos-people-desc {
    margin-top: 0px;
}

#global-nav.styleguide-global-nav + #Main table tbody tr td#Hint .ThinSeparated.photos-people-desc>h4 {
    position: relative;
    display: inline-block;
    width: 419px !important;
    height: 25px;
    left: 5px !important;
    margin-bottom: 0px !important;
    top: 0px !important;
border-bottom: 1px solid yellow !important;
}
#global-nav.styleguide-global-nav + #Main table tbody tr td#Hint .ThinSeparated.photos-people-desc>h4 + p:only-of-type {
    position: relative !important;
    display: inline-block !important;
    margin-top: 10px !important;
    top: 0 !important;
}
#global-nav.styleguide-global-nav + #Main table tbody tr td#Hint .ThinSeparated.photos-people-desc + div{
    position: relative !important;
    display: inline-block !important;
    margin-top: -5px !important;
    margin-bottom: 10px !important;
}

/* == END == URL PREF - ABOUT PHOTO OF === */
}
