@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jaynaylor.com") {
body {
background:#fff !important;
}
#container, .sidebar, .content, .content h1, .content h2, .content h3, .content p, .content p.posted {
background:#fff !important;
text-align:center !important;
font-family:Vverdana, Hhelvetica, Arial, Sans-Serif;
}
#container {
margin-left:auto;
margin-right:auto;
}
.content p.posted {
border-top:1px solid #464646 !important;
color:#464646 !important;
}
.sidebar h3 {
font-size:15px;
}
.sidebar img {
display:none;
}
.sidebar u {
text-decoration:none;
}
#calendar {
color:#464646;
}
a {
text-decoration:none !important;
color:#464646 !important;
}
a:link {
text-decoration:none !important;
color: #464646 !important;
}
a:visited {
text-decoration:none !important;
color: #464646 !important;
}
a:active {
text-decoration:none !important;
color: #2d2d2d !important;
}
a:hover {
text-decoration:none !important;
color: #2d2d2d !important;
}
#banner a {
color:#fff !important;
}
#banner a:hover {
color:#fff !important;
}
#container {
border:0 !important;
width:800px;
background:#fff !important;
margin-left:auto !important;
margin-right:auto !important;
text-align:center;
}
#banner {
background:#888 !important;
-moz-border-radius:0px 0px 15px 15px !important;
}
}
