css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com"), domain("ilike.com")  {
 body {
    background: url('http://static.tumblr.com/7vwda4v/GKalpo863/ljs.jpg') center center #000000 no-repeat fixed !important;
  }
#content{background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
background-color:transparent;}
#contentCol, #contentCurve, #pageFooter {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
background-color:transparent;
boder:none;
}
p,a,li{color:#fff;}
a:hover{text-shadow: 0 0 5px #fff;}
.uiStream .uiStreamMessage {
color: #fff;
font-size: 11px;
font-weight: normal;
}
h1, h2, h3, h4, h5, h6 {
font-size: 13px;
color: #fff;
}

.uiUfi .ufiItem {
background-color: #090909;
border-bottom: 1px solid #333;
margin-top: 2px;
padding: 5px 5px 4px;
}

.uiUfi .ufiNub i {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
background-color:none;

}
 .MessagingReadHeader{background-color:#111;}
.profile-picture{box-shadow: 0 0 5px #fff;}

.ginormousProfileName {
color: #FFF;}

.uiMorePagerLight .uiMorePagerPrimary, .uiMorePagerLight .uiMorePagerLoader {
padding: 6px 5px 10px;
border-top: 1px solid #E5EAF1;
background-color: #090909;
}

.uiMorePagerLight .uiMorePagerPrimary:hover, .uiMorePagerLight .uiMorePagerLoader:hover {
background-color: #111;
}

.uiHeaderSection, .uiSideHeader {
background-color: #090909;
border-bottom: none;
border-top: solid 1px #E2E2E2;
padding: 4px 5px 5px;
}

.uiHeader h3, .uiHeader h4 {
color: #FFF;
font-size: 11px;
}

.uiSideNav .item, .uiSideNav .subitem {color:#FFF;}

.uiSideNav .item, .uiSideNav .subitem {
display: block;
border-bottom: 0px solid #000;
line-height: 13px;
padding: 3px 8px 4px 28px;
text-decoration: none;
}

.uiSideNav .item:hover, .uiSideNav .subitem:hover{

display: block;
background-color:#111;
border-bottom: 0px solid white;
line-height: 13px;
padding: 3px 8px 4px 28px;
text-decoration: none;
}

.uiSideNav .item:hover, .uiSideNav .item:active, .uiSideNav .item:focus, .uiSideNav .subitem:hover, .uiSideNav .subitem:active, .uiSideNav .subitem:focus{background-color:#111;
border-bottom: 0px solid white;}


#footerContainer {
background-color:none;
border-top: 1px solid #EEE;
margin-bottom: 30px;
padding: 8px;
position: relative;
}
.uiBoxLightblue {
background-color: #111;
border: 1px solid #333;
color:#fff;
}
.uiBoxGray {
background-color: #111;
border: 1px solid #333;
}
.uiSideNav .open .selectedItem{background-color:#111;
border-bottom: 0px solid white;
background-image:none;
}

.uiSideNav .selectedItem .item,.uiSideNav .selectedItem .item:hover,.uiSideNav ul .selectedItem .subitem,.uiSideNav ul .selectedItem .subitem:hover{background-color:#111;font-weight:bold}
#contentCol {
border-left: 0px solid #333;}

.fbPhotoTheater .photoInfo {
background: black;
border: solid black;
height: 125px;
border-width: 3px 1px;
}

.uiHeader h2 {
color:#FFF;}

.groupProfileHeaderWash {
background: #111;
border-bottom: 1px solid #333;}

#mainContainer {
border: 0px solid #333;
}
.hasRightCol #contentArea {margin-left:1px solid #333;}
}
