css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') {







#dashboard_index{background:#2C4762 url(http://fc04.deviantart.net/fs71/f/2011/033/9/a/community_wallpaper_by_tomoxnam-d38m57o.png) repeat fixed 0% 0% !important;}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(0,0,0,0.5)!important;}







ol#posts li.notification{background-color: #456179 !important;



border-bottom: 1px solid #2F465C !important;



color: #A8BCCB !important;}







#left_column .dashboard_options_form {



   background: url('http://assets.tumblr.com/images/white_20.png')#1a1a1a;



}







#right_column a#dashboard_switch_blog_menu_current:hover {



   background: transparent;



}







#nav .nav_item.active{



   background-color::#2C4762;



}







#left_column #posts .post .permalink{



   background-color::#2C4762;

}







#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{



   background-color:rgba(255,193,37,0.8);



}







}
