css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.toodledo.com") {

  body
  {
    color: #D9D9D9 !important;
    background-image: none !important;
    background-color: #111 !important;


    /*useful for "inherit" */
    border-color: #111 !important;
  }

  body a
  {
    color: #AAF !important;
  }

  /* --- BANNER --- */

  #banner
  {
    background-image: none !important;
    background-color: #222 !important;
  }

  /*fyi, the "tasks" and "notebook" image have their own bg img.*/
  #banner a
  {
    background-image: none !important;
  }

  #banner a:hover
  {
    text-decoration: underline !important;
  }

  ul#organizemenu > li, ul#organizemenu > li a, ul#toolsmenu > li, ul#toolsmenu > li a
  {
    background-image: none !important;
    background-color: #000 !important;
    color: #FFF !important
  }

  #banner > div#welcome
  {
    color: #DDD !important;
  }

  #banner > div#welcome > select
  {
    background-color: #222 !important;
    border-color: #888 !important;
  }

  /* ---- Everything under banner ---- */
  /* --- Layout Blocks --- */
  #main
  {
    background-image: none !important;
    background-color: #111 !important;
  }

  /* footer */
  #bottom
  {
    border: none;
    background-image: none !important;
    background-color: inherit !important;
  }

  /* menu */
  #left_side, #left_side div
  {
    border-color: #222 !important;
    background-image: none !important;
    background-color: #222 !important;
    color: #EEE !important;
  }

  /* rchunk contains all toolbars and tasks */
  #rchunk
  {
    background-image: none;
    background-color: #111;
  }


  /* --- 1ST TOOLBAR --- */
  /* includes "add task" form */

  #rchunktop, #rchunktop div
  {
    background-color: #0B0B19 !important;
    border-color: #0B0B19 !important;
  }

  /* drop down menus */
  .popmenu
  {
    background-color: #002 !important;
  }

  /*form inputs in the "add task" form */
  #rchunktop select,#rchunktop textarea, #rchunktop input
  {
    background-color: #113 !important;
  }

  /* "add task" button */

  #rchunktop div#tlbr_new
  {
    background-image: none !important;
    background-color: inherit !important;
    color: #CCC !important;
  }

  #rchunktop > div#head > div#tlbr_new a
  {
    color: #FFF;
  }

  #rchunktop div#tlbr_new:hover
  {
    background-image: none !important;
    background-color: inherit !important;
    color: #FFF !important;
  }

  /* --- 2ND TOOLBAR --- */
  #rchunk > div#colhead > div, #rchunk div#colhead
  {
    background-color: #111 !important;
    border-color: #111 !important;
  }

  #rchunk #tb_cust
  {
    background-image: none !important;
    background-color: inherit !important;
  }

  /* --- SEPARATORS --- */
  #rchunk > div#tasks > div.sep
  {
    background-image: none !important;
    background-color: #10101C !important;
    border-color: #10101C !important;
  }

  /* --- TASKS --- */
  #rchunk > div#tasks
  {
   /*any color changed here only affects the "displayed tasks count" bar at the bottom of the tasks list */
    border-color: #888 !important;
    background-color: inherit;
  }

   /*the blocks used for tasks */
  #rchunk > div#tasks div
  {
   background-color: #111 !important;
  }

  #rchunk > div#tasks .note
  {
    color: #EEE !important;
  }

  /* -- field-specific color settings -- */
  /* these task fields require their own special color setting */

  #rchunk > div#tasks .dim /*folder info*/
  { color: #CCC !important; }

 /*higher = more urgent */
  #rchunk > div#tasks .date0
  { color: #EEE !important; }
  #rchunk > div#tasks .date1
  { color: #AFF !important; }
  #rchunk > div#tasks .date2
  { color: #7FF !important; }
  #rchunk > div#tasks .date3
  { color: #1FF !important; }

  /* (-1 priority should be dim on dark bgs) */
  #rchunk > div#tasks .pri-1
  { color: #AAA !important; }

 /* (making 0 and 1 priority readable) */
  #rchunk > div#tasks .pri0
  { color: #EEE !important; }
  #rchunk > div#tasks .pri1
  { color: #DDA !important; }

 /* -- Forms launched from within a row -- */

  #rchunk > div#tasks select
  { background-color: #222 !important; }

 /* Calendar Selectors */
 /* I get the impression that these are not created within a task row's div */
 #duediv
  { background-color: #222 !important; }

 #duediv select#dmod, #duediv input, #startdiv, #startdiv input
  {
    border-color: #777 !important;
    background-image: none !important;
    background-color: #111 !important;
  }

  #duediv #protocal, #duediv #protocal select, #startdiv #protocal2, #startdiv #protocal2 select
  {
    background-color: #CCC !important;
    color:black;
  }

  /* -- Task row behavior when you put mouse over it -- */

  #rchunk > div#tasks .cols:hover
  {
    background-color: #122 !important;
  }

/* rectangle that contains a field of a task */
  #rchunk > div#tasks .cols:hover .dett
  {
    background-color: #111A1A !important;
  }

/*background of the value of the field of a task*/
  #rchunk > div#tasks .cols:hover span
  {
    background-color: #111 !important;
  }

/*rectangle that contains note of a task. has only
one bg. */
  #rchunk > div#tasks .cols:hover .note
  {
    background-color: #111A1A !important;
  }

/* ---- OTHER PAGES ---- */

  /* this stuff mostly comes up in options */
  #rchunk h1, #rchunk h2
  {
    color: #BAF !important;
  }

  #rchunk #homeleft, #showcase div, #showcase h4
  {
    color: #EEE !important;
  }

  table input
  {
    background-color: #112 !important;
    color:white !important;
  }

  #rchunk th
  {
    background-color: #222 !important;
  }

  #rchunk td, #rchunk td select, #rchunk td textarea, #rchunk td input, #rchunk td div
  {
    background-color: #000 !important;
    color: #EEE !important;
  }

  /* === Notebooks === */

  /* #notebooks > div.row, #notebooks > div.row .ndetd
  {  background-color: #100;  } */

  #notebooks > div.row:hover, #notebooks > div.row:hover .ndetd
  {
    background-color: #090009 !important;
  }

  #notebookActions
  {
    background-color: #0B0B19 !important;
    border: none !important;
  }

  #notebookTitle
  {
    background-color: #0B0B19 !important;
  }

  #notebookTitle input, #notebookTitle select
  {
    background-color: #113 !important;
  }

  #notebookText, #notebookText textArea
  {
    background-color: #00000C !important;
  }

  /* === Forum === */
  #rchunk input, #rchunk select, #rchunk textarea
  {
    background-color:inherit !important;
  }


  /* === Organize === */
  #rchunk > div.addbox, #rchunk > div.addbox input
  {
    background-color: #0B0B19 !important;
    color: #EEE;
  }

  #rchunk > div.sep
  {
    background-color: #222 !important;
    border: none !important;
    color: #EEE !important;
  }

  form#goalform input
  {
    border-color: #111 !important;
  }
  form#goalform div.sep
  {
    background-color: #222 !important;
    border-color: #222 !important;
    color: #EEE !important;
  }
  form#goalform div.cols goalrow
  {
    background-color: #111 !important;
  }
  form#goalform div.goalinset
  {
    background-color: #222 !important;
  }

  /* Manage Locations */
  div#locationlist > div:hover
  {
    background-color: #001 !important;
  }

  div#locationlist > div:hover span
  {
    background-color: inherit !important;
  }

  /* === Tools === */
  #rchunk > div.detail > b
  {
    color: #9bF !important;
  }
  /* import and export page */
  
  #rchunk > div.connectbox
  {
    background-color: #113 !important;
  }

  /*third party apps */

  #rchunk > .appcat > .applist
  {
    background-color: #0C0011 !important;
  }
  #rchunk > .appcat > .applist > div.approw3
  {
    background-color: inherit !important;
  }

  /* === Help === */
  #rchunk div.simplebox
  {
    background-color: #222 !important;
  }

  #rchunk > div#help
  {
    background-image: none !important;
    background-color: #011 !important;
  }

  #rchunk > div#help > div#answer
  {
    background-color: #011 !important;
    color: #EEE;
  }
}
