/* From http://nicolasgallagher.com/pure-css-speech-bubbles/ */


/* THE SPEECH BUBBLE
------------------------------------------------------------------------------------------------------------------------------- */

.triangle-border

 {
	position:relative;
	/*
	padding:15px;
	*/
	margin:1em 0 3em;
	border:5px solid #7cc6de !important;
	color:#333;
	background:#222222 !important;
	/* css3 */
	-webkit-border-radius:10px;
	-moz-border-radius:10px;
	border-radius:10px;
        background:-webkit-gradient(linear, 0 0, 0 100%, from(#222222), to(#222222)) !important;
	background:-moz-linear-gradient(#222222, #222222) !important;
	background:-o-linear-gradient(#222222, #222222) !important;
	background:linear-gradient(#222222, #222222) !important;

}


/* creates the larger triangle */
.triangle-border.left:before

 {
	top:10px; /* controls vertical position */
	bottom:auto;
	left:-30px; /* value = - border-left-width - border-right-width */
	border-width:15px 30px 15px 0;
	border-color: transparent #7cc6de !important;
        
      
        
 }

/* creates the smaller  triangle */
.triangle-border.left:after {
	top:16px; /* value = (:before top) + (:before border-top) - (:after border-top) */
	bottom:auto;
	left:-21px; /* value = - border-left-width - border-right-width */
	border-width:9px 21px 9px 0;
	border-color:transparent #fff;
}

/* Variant : right
------------------------------------------ */

/* creates the larger triangle */

.triangle-border.right:before 

{
	top:10px; /* controls vertical position */
	bottom:auto;
    left:auto;
	right:-30px; /* value = - border-left-width - border-right-width */
	border-width:15px 0 15px 30px;
	border-color:transparent #7cc6de !important;
        
}

/* creates the smaller  triangle */
.triangle-border.right:after 

{
	top:16px; /* value = (:before top) + (:before border-top) - (:after border-top) */
	bottom:auto;
    left:auto;
	right:-21px; /* value = - border-left-width - border-right-width */
	border-width:9px 0 9px 21px;
	border-color:transparent #fff;
        
}
}
