css:
/** version 1.0 **/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("freekick.org") {
#freekick{background: #292a2e !important;}
#freekick hr {border-color: #35363a !important;}
#freekick th {background-color: #292a2e !important;}
#freekick .header-title,
#freekick h1,
#freekick h2,
#freekick h3,
#freekick h4,
#freekick .header-title a,
#freekick h1 a,
#freekick h2 a,
#freekick h3 a,
#freekick h4 a { color: #77b7e0 !important;}
#freekick td:not(.expense):not(.income) a, #freekick .link, #freekick .textlink, #freekick div a:not(.off):not(.on) {color: #fdfdfd !important;}


.form-trend{
  position: relative !important;
  top: 2px !important;}

.page-box{background-color: #353a3c !important;}
.page-box-header {
  background: none !important;
  color: #77b7e0 !important;
  background: -moz-linear-gradient(top,#4b5157,#3b4044) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#4b5157), to(#3b4044)) !important;
  background: -o-linear-gradient(#4b5157, #3b4044) !important;}
.page-box-header a{color: #fff !important;}
.page-box-header a:hover{text-decoration:underline !important;}
.page-box-footer {
  background: none !important;
  background: -moz-linear-gradient(top,#4b5157,#3b4044) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#484), to(#3b4044)) !important;
  background: -o-linear-gradient(#4b5157, #3b4044) !important;}
.page-box-content-inner{color: #f7f7f7 !important;}

.page-header {
  background-color: #292a2e !important;
  background: -moz-linear-gradient(top,#4b5157,#3b4044 85px,#3b4044 85px,#4b5157) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#3a773a), color-stop(73%,#484), color-stop(73%,#242), to(#363)) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#3a773a), color-stop(85px,#484), color-stop(85px,#242), to(#363)) !important;
  background: -o-linear-gradient(#3a773a,#484 73%,#242 73%,#363) !important;}
.page-blue-stripe{background: none !important;}
.page-info{color:#b3b4b4 !important; font-size:11px !important;}
.page-button{color:#99b8cb !important;}
.page-separator-l, .page-separator-r{border: 0 !important;}
a.page-button:active, a.page-button:focus, a.page-button:hover, .toggler a.page-button {
  background: none !important;
  background: -moz-linear-gradient(top,#4b5157,#3b4044) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#353), to(#474)) !important;
  background: -o-linear-gradient(#4b5157, #3b4044) !important;}
#language-selector {border: 1px solid #2d2e32 !important;}


.menu {background-color: #292a2e !important;}
.menuLink {
  color: #fff !important;
  border-color: #34383a!important;
  }
.menuItem {
  background-color: #353a3c !important;
  border-color: #1a1a1b !important;
  }
.menuItem:hover {
  border-color: #1a1a1b !important;
  }
.menuItem.inactive:hover {
  border-color: #1a1a1b !important;
  background-color: transparent !important;
}
.inactive .menuLink {
  border-color: #1a1a1b !important;
}
.menuLink:focus,
.menuLink:active {background-color: #484 !important;}
.menuItem:hover > .menuLink {
  background-color: #43484d !important;
  border-color: #43484d !important;
  }
.menuItem .menu {border-color: #242529 !important;}

.menu-icon {
  background: none !important;
  }
.menuLink span:before {
  content: "» ";
  color: #8a8c8d !important;
  font-weight:bold !important;
  }
.menuItem:hover > .menuLink span:before { 
  color: #cacbcb !important;
}



.trophyPlayer{border-top-color: #2e2f33 !important;}

.club,
.user,
.player,
.nation,
.division,
.competition,
.match,
.rank,
.pos { color: #fff !important; }
.bid-inactive{color: #aaa !important;}
.expense a, .expense, .off, .offline {color: #f51 !important;}
.income a, .income, .on, .online {color: #5e5 !important;}
.transfer{color: #ffc !important;}
.desired-position { color: #ffc !important; }

.form-prediction-denom{background-color: #353a3c !important;}

.awesome {color: #d61 !important;}
.brilliant {color: #f83 !important;}
.superb {color: #f96 !important;}
.excellent {color: #f9c !important;}
.good {color: #c3c !important;}
.decent {color: #fff !important;}
.weak {color: #3cc !important;}
.poor {color: #ff0 !important;}
.awful {color: #f00 !important;}

.age-old { color:#e72 !important; }
.drop { color:#c73 !important; }
.pot { color: #5e3 !important; }
.abovepot { color: #ec0 !important;}
.age-senior { color:#fff!important; }
.age-youth-0 { color:#8ce!important; }
.age-youth-1, .age-youth { color:#3ad!important; }
.age-youth-2 { color:#8ce!important; }
.age-youth-3 { color:#3ad!important; }
.age-youth-4 { color:#8ce!important; }
.age-youth-5 { color:#3ad!important; }
.age-retired { color: #f00 !important; } 


.header-sub-title{color: #fff !important;}
.header-inner {border-bottom: 2px solid #3d4246 !important;}
.header-outer {border-bottom: 1px solid #3d4246 !important;}
.tab-active {
  background: none !important;
  background: -moz-linear-gradient(top,#323638,#2a2d2f) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#f00), to(#484)) !important;
  background: -o-linear-gradient(#363, #484) !important;}
.page-tab-separator {
  background: none !important;
  background-color: #2d2e32 !important;}
.loading {background-color: #292a2e !important}

#site-help{background: none !important;}
#site-help:before{
  content:"?";
  font-size: 20px !important;
  position: relative;
  top: 5px;
  right: 2px;
  font-weight: normal !important;
}
#site-help:hover{
  text-decoration: none !important;
}
.button:hover {
  background: none !important;
  border-color: transparent !important;
}
.button-active,
.button-active:hover,
.button:active,
.button:focus {
  background: none !important;
  border-color: transparent !important;
}
.button-expanded{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-expanded:before{
  content:"-";
  font-size: 20px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
  position: relative !important;
  left: 4px;
}
.button-collapsed{
  background: none !important;
  text-indent: 0px !important;
  font-size: 0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-collapsed:before{
  content:"+";
  font-size: 20px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
}
.button-remove{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-remove:before{
  content:"×";
  font-size: 12px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
}
.button:hover .button-remove:before{
  background: none !important;
  color: #f33 !important;
}
.button-edit{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-edit:before{
  content:"✎";
  font-size: 13px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
}
.button:hover .button-edit:before{
  background: none !important;
  color: #0f0 !important;
}
.button-settings{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-settings:before{
  content:"⚙";
  font-size: 15px !important;
  color: #2d2e32;
  font-weight: bold !important;
  font-style: normal !important;
  position: relative !important;
  top: 4px !important;
}
.button:hover .button-settings:before{
  background: none !important;
  color: #0f0 !important;
}

.multiple-select{
  color: #000 !important;
  }
.multiple-select li:hover{
  background-color: #cec !important;
  }

#page-copyright {color: #f!important;}


label.alternateRow,
div.alternateRow,
ul.alternateRow,
li.alternateRow,
a.alternateRow { border-width: 0 !important; }
tr.alternateRow th,
tr.alternateRow td { border-width: 0 !important; }

label.alternateOdd,
div.alternateOdd,
li.alternateOdd,
ul.alternateOdd,
a.alternateOdd,
tr.alternateOdd td { 
  background: #282c2f !important;}

label.alternateEven,
div.alternateEven,
ul.alternateEven,
li.alternateEven,
a.alternateEven,
tr.alternateEven td { 
  background: #2d2e32 !important;}

label.alternateMarked,
div.alternateMarked,
li.alternateMarked,
a.alternateMarked,
tr.alternateMarked td, 

label.alternateOddMarked,
div.alternateOddMarked,
li.alternateOddMarked,
a.alternateOddMarked,
tr.alternateOddMarked td { 
  background: #1b1e20 !important;
}

label.alternateEvenMarked,
div.alternateEvenMarked,
li.alternateEvenMarked,
a.alternateEvenMarked,
tr.alternateEvenMarked td { 
  background: #1b1e20 !important; 
}

label.alternateOdd:hover,
div.alternateOdd:hover,
li.alternateOdd:hover,
a.alternateOdd:hover,
tr.alternateOdd:hover td,
label.alternateEven:hover,
div.alternateEven:hover,
li.alternateEven:hover,
a.alternateEven:hover,
tr.alternateEven:hover td {
  background: -moz-linear-gradient(top,#a7b2bb,#6f777d) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#007000), to(#114b11)) !important;
  background: -o-linear-gradient(#f00, #114b11) !important;
  border: 0 !important;}

label.alternateEvenMarked:hover,
div.alternateEvenMarked:hover,
li.alternateEvenMarked:hover,
a.alternateEvenMarked:hover,
tr.alternateEvenMarked:hover td,
label.alternateOddMarked:hover,
div.alternateOddMarked:hover,
li.alternateOddMarked:hover,
a.alternateOddMarked:hover,
tr.alternateOddMarked:hover td{
  background: -moz-linear-gradient(top,#8b8b8c,#0a703a) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#284), to(#0a703a)) !important;
  background: -o-linear-gradient(#284, #0a703a) !important;
  border: 0 !important;}

label.alternateRowHeader,
div.alternateRowHeader,
ul.alternateRowHeader,
li.alternateRowHeader,
tr.alternateRowHeader th {
  border-width: 0 !important;
  background: #3d4246 !important;}

.floating-row-header{
  border-width: 0 !important;
  background: #2d2e32 !important;}
.gridItemHeader{
  border-width: 0 !important;
  background: #494e54 !important;}
.gridItem{
  border-width: 0 !important;
  background: #2d2e32 !important;}

td.separatorB hr {
  border-color: #2d2e32 !important;
  }
.small-news-item-message{
  background: #3d4246 !important;
  border: 0 !important;}
.ltr .separatorR,
.rtl .separatorL,
.ltr .separatorL,
.rtl .separatorR { border-color: #242 !important; }


.matchreport-legendary {color: #d61 !important; font-style: normal !important;}
.matchreport-masterful {color: #d61 !important; font-style: normal !important;} 
.matchreport-unbelievable {color: #d61 !important; font-style: normal !important;} 
.matchreport-awesome {color: #d61 !important; font-style: normal !important;} 
.matchreport-brilliant {color: #f83 !important; font-style: normal !important;} 
.matchreport-superb {color: #f96 !important; font-style: normal !important;} 
.matchreport-excellent {color: #f9c !important; font-style: normal !important;} 
.matchreport-good {color: #c3c !important; font-style: normal !important;} 
.matchreport-decent {color: #fff !important; font-style: normal !important;} 
.matchreport-weak {color: #3cc !important; font-style: normal !important;} 
.matchreport-poor {color: #ff0 !important; font-style: normal !important;} 
.matchreport-awful {color: #f00 !important; font-style: normal !important;} 

.matchreport-right_spot {color: #d61 !important; font-style: normal !important;}
.matchreport-prepared {color: #d61 !important; font-style: normal !important;}
.matchreport-there {color: #f9c !important; font-style: normal !important;}
.matchreport-close {color: #3cc !important; font-style: normal !important;}
.matchreport-too_late {color: #f00 !important; font-style: normal !important;}
.matchreport-dreadful {color: #f00 !important; font-style: normal !important;}

.matchreport-ready{color: #d61 !important; font-style: normal !important;}
.matchreport-hesitant{color: #f9c !important; font-style: normal !important;}
.matchreport-fooled{color: #3cc !important; font-style: normal !important;}
.matchreport-totally_wrong{color: #f00 !important; font-style: normal !important;}

.matchreport-net_wide_open {color: #d61 !important; font-style: normal !important;}
.matchreport-totally_free {color: #d61 !important; font-style: normal !important;}

.min, .matchreport-penaltykicks, .matchreport-halftime, .matchreport-penaltyround {
	color: #c80 !important;
	font-weight: bold !important;
}
.matchreport-goalattempt, .matchreport-throwin, .matchreport-cornerkick, .matchreport-freekick, .matchreport-penaltykick, .matchreport-playcontinued, .matchreport-counterattack { 
	color: #4f4 !important;
	font-weight: bold !important;
}
.matchreport-goal{
	color: #d61 !important;
	font-weight: bold !important;
	font-size: 13px !important;
}

.players-tabs li{
  padding: 0px !important;
  margin: 0 2px !important;
  -moz-border-radius-topright: 5px;
  -moz-border-radius-topleft: 5px;
  -webkit-border-top-left-radius: 5px;
  -webkit-border-top-right-radius: 5px;
}
.players-tabs a{
  padding: 9px 9px 7px 8px !important;
  font-style: normal !important;
  -moz-border-radius-topright: 5px;
  -moz-border-radius-topleft: 5px;
  -webkit-border-top-left-radius: 5px;
  -webkit-border-top-right-radius: 5px;
}
.players-tabs li.players-selected{
  background: none !important;
  background-color: #43484d !important;
  margin-left: 4px !important;
}
.players-tabs li.players-selected a{
  background: none !important;
  background-color: #727a82 !important;
}
.players-tabs li {
  background: none !important;
  background-color: #353a3c !important;
}
.players-tabs li a {
  background: none !important;
  background-color: #494f52 !important;
}
.players-wrap {
  border-top: 3px solid #727a82!important;
} 
	

.information{color: #fff !important;
  border: 0 !important;
  background: #3d4246 !important;
}

.information-blue,
.information-darkblue,
.information-darkgreen,
.information-featured,
.information-grey {
  border: 0 !important;
  background: #565c5e !important;
}

.information-error,
.information-red {
  border: 0 !important;
  background: #933 !important;
}

.information-notice,
.information-orange {
  border: 0 !important;
  background: #41b755 !important;
}

.information-ok,
.information-green {
  border: 0 !important;
  background: #006600 !important;
}

}
