css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("anathema-guild.de") {

#header {
	background-image: none !important;
}
#footer {
	background-image: url("http://img593.imageshack.us/img593/8039/footerbgs.jpg") !important;
}

span.corners-top,
span.corners-bottom {
	background-image: url("http://img845.imageshack.us/img845/9730/cornersleft.png") !important;
}

span.corners-top span,
span.corners-bottom span {
	background-image: url("http://img692.imageshack.us/img692/2371/cornersrighti.png") !important;
}

.forabg, .forumbg {
	background-color: #303030 !important;
	background-image: none !important;
}

li.row {
	border-bottom: none !important;
	border-top: 1px solid #777777 !important;
}

ul.forums li.row:first-child,
ul.topiclist li.row:first-child {
	border-top: none !important;
}

li.header dt {
	font-size: 1.3em !important;
	color: #B97611 !important;
}

li.header dt a {
	color: #B97611 !important;
}

ul.topiclist li {
	color: #888888 !important;
}

li.row strong {
	color: #888888 !important;
}

ul.topiclist li.row dt a.subforum.read {
	color: #AAAAAA !important;
}


a,
a:active,
a:link,
a:visited,
a:hover,
a.button1,
input.button1,
input.button3,
a.button2,
input.button2,
select,
div#roster a,
.pagination span strong {
    color: #B97611 !important;
}
a.forumtitle {
	color: #AAAAAA !important;
}
a.topictitle {
	color: #AAAAAA !important;
}
.postbody h3 a {
	color: #AAAAAA !important;
}
a.postlink {
	color: #CCCCCC !important;
}


}
