}
.button-active,
.button-active:hover,
.button:active,
.button:focus {
  background: none !important;
  border-color: transparent !important;
}
.button-expanded{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-expanded:before{
  content:"-";
  font-size: 25px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
  position: relative !important;
  left: 4px;
}
.button-collapsed{
  background: none !important;
  text-indent: 0px !important;
  font-size: 0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-collapsed:before{
  content:"+";
  font-size: 20px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
}
.button-remove{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-remove:before{
  content:"×";
  font-size: 12px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
}
.button:hover .button-remove:before{
  background: none !important;
  color: #f33 !important;
}
.button-edit{
  background: none !important;
  text-indent: 0px !important;
  font-size:0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-edit:before{
  content:"✎";
  font-size: 13px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
}
.button:hover .button-edit:before{
  background: none !important;
  color: #0d0 !important;
}
.button-settings{
  background: none !important;
  text-indent: 0px !important;
  font-size: 0;
  color: transparent !important;
  text-decoration:none !important;
  }
.button-settings:before{
  content:"*";
  font-size: 28px !important;
  color: #fff;
  font-weight: bold !important;
  font-style: normal !important;
  position: relative !important;
  top: 4px !important;
}
.button:hover .button-settings:before{
  background: none !important;
  color: #0d0 !important;
}
.multiple-select{
  color: #000 !important;
  }
.multiple-select li:hover{
