/* wiersze naprzemienne w tabelach */

.alternateRow {
    margin-top: 2px;
    padding: 1px 6px 0;
    line-height: 1.6em; 
    border-width: 0px !important; }
a.alternateRow { display: block; }

.alternate td,
.alternate th {
    position: relative;
    border: 0 solid transparent;
    border-width: 1px 0; }

.alternateRowHeader,
.alternate th {
  border: 0 !important; 
  background-color: #080  !important;
  background-image: none !important;
}

div.alternateRowHeader {
    border-width: 0 !important;
    background: -moz-linear-gradient(top,#060,#000) !important;
}

.alternate .odd,
.alternate .odd td,
.alternateRow.alternateOdd,
.alternateOdd td { 
  background-color: #005a00 !important;
  border: 0 !important;
}  

.alternate .even,
.alternate .even td,
.alternateRow.alternateEven,
.alternateEven td { 
  background-color: #060 !important; 
  border: 0 !important;
}

.alternateRow:hover,
tr.alternateRow:hover td { 
  background-color: #040 !important; 
  border: 0px !important;
}
.alternateRow:hover .separatorB,
tr.alternateRow:hover .separatorB {
  background-color: transparent;
  border-color: transparent;
}

.alternate .marked,
.alternate .marked td, 
.alternate .odd.marked td,

.alternateRow.alternateHighlight,
.alternateRow.alternateMarked,
.alternateHighlight td,
.alternateMarked td, 
.alternateOddMarked td,
.odd.alternateMarked td { 
  background-color: #030 !important;
  border: 0 !important;
}

.alternate .even.marked,
.alternate .even.marked td,
.alternateRow.alternateEvenMarked,
.alternateEvenMarked td,
.even.alternateMarked td{ 
  background-color: #030 !important;
  border: 0 !important;
}

.alternate .marked:hover, 
.alternate .odd.marked:hover,
.alternate .even.marked:hover,
.alternate .marked:hover td, 
.alternate .odd.marked:hover td,
.alternate .even.marked:hover td,

.alternateHighlight:hover,
.alternateMarked:hover, 
.alternateOddMarked:hover,
.alternateEvenMarked:hover,
tr.alternateHighlight:hover td,
tr.alternateMarked:hover td, 
tr.alternateOddMarked:hover td,
tr.alternateEvenMarked:hover td {
  background-color: #020 !important;
  border: 0 !important;
}

.body > .row:hover,
.row:hover > td,
.body > .marked,
.marked > td,
.body > .uiToggleOpen,
.uiToggleOpen > td { background-color: #040 !important; }

/* kolory w tabeli kandydatów do rozgrywek specjalnych */
tr.odd td {background-color: #060 !important;}   
tr.even td {background-color: #050 !important;}
tr.marked td {background-color: #030 !important;} 

/* kropki oddzielające wiersze w tabeli kandydatów do rozgrywek specjalnych oraz w tabelach tematów na forum */
table.standard-list tr th, table.standard-list tr td,
table.highlightable tr td, table.highlightable tr th {
    border-bottom: 1px dotted #696 !important;}
   
/* kolor tła chmurek z podpowiedziami*/
#WzBoDy {background: #2cf !important;}

/* VIP-sklep */
.row {background-color: #060 !important;} 
.available {color: #0b0 !important;}

/* nagłówki tabeli na stronie mentorów */
table.alternate {color: #fff !important;}

/* kolor punktów za osiągnięcia */
.achievement-points {color: #2af !important;}

/* pinezki */
.shortListNotSelected { background: #040 !important;}
.shortListSelected { background: #0b0 !important;}

/* nowości w instrukcji */
.manual_new {color: #02f !important;}

/* licznik czasu na str. puli sparingowej */
.timer {color: #fff !important;}

