css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("freekick.org") {

.form-trend{
  position: relative !important;
  top: 2px !important;}
.uiChoiceChecked { background-color: #02f !important;}
.uiChoiceItem:hover {background: #0c0 !important;}


.page-box-header {
  background: none !important;
  color: #fff !important;
  background: -moz-linear-gradient(top,#060,#000) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#060), to(#000)) !important;
  background: -o-linear-gradient(#060, #000) !important;}
.page-box-header a{color: #fff !important;}
.page-box-header a:hover{text-decoration:underline !important;}

.page-header {
  background-color: #080 !important;
  background: -moz-linear-gradient(top,#060,#080 85px,#060 85px,#000 116px,#050 116px,#000) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#060), color-stop(73%,#080), color-stop(73%,#060), to(#000)) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#060), color-stop(85px,#080), color-stop(85px,#060), to(#000)) !important;
  background: -o-linear-gradient(#060,#080 73%,#060 73%,#000) !important;}
.page-blue-stripe{background: none !important;}
.page-info, #fk-clock{color:#fff !important; font-size:11px !important;}
.page-button{color:#fff !important;}
.page-separator-l, .page-separator-r {border-color: #070 !important;}

a.page-button:active, a.page-button:focus, a.page-button:hover, .toggler a.page-button {
  background: none !important;
  background: -moz-linear-gradient(top,#0a0,#050) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#0a0), to(#050)) !important;
  background: -o-linear-gradient(#0a0, #050) !important;}
#language-selector {border: 1px solid #030 !important;}

.menuItem:hover > .menuLink {
  background-color: #0a0 !important;
  border-color: #0a0 !important;
  }
.menu-icon {
  background: none !important;
  }
.tab-active {
  background: none !important;
  background: -moz-linear-gradient(top,#0a0,#050) !important;
  background: -webkit-gradient(linear, left top, left bottom, from(#0a0), to(#050)) !important;
  background: -o-linear-gradient(#0a0, #050) !important;}

#fk-clock{color:#fff !important; font-size:11px !important;}

.matchreport-legendary {
