css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.reddit.com/r/Minecraft"),
url-prefix("http://reddit.com/r/Minecraft"),
url-prefix("http://www.reddit.com/r/minecraft"),
url-prefix("http://reddit.com/r/minecraft") {

body {
  background: url(data:image/png;base64,) !important;
}

hr {
  border: none !important;
  border-bottom: 1px dotted #777 !important;
}

textarea:hover, textarea:focus, input[type="text"]:hover,  input[type="text"]:focus {
  border-style: solid !important;
  border-color: #808080 !important;
  -moz-box-shadow: 0 0 5px #ddd !important;
}

input[type="submit"], input[type="button"], input[type="reset"], button {
  background: url(data:image/png;base64,) 0 -2px !important;
  padding: 2px 7px !important;
  color: #fff !important;
  font-weight: bold !important;
  cursor: pointer !important;
  border: 1px solid #835C37 !important;
  -moz-appearance: none !important;
/*  text-shadow: 1px 1px 0 #886034 !important; */
}

input[type="submit"]:hover, input[type="button"]:hover, input[type="reset"]:hover, button:hover {
  -moz-box-shadow: 0 0 5px #8E7352 !important;
}

img[src*="static/noimage.png"] {
  padding-left: 70px !important;
  width: 0 !important;
  height: 50px !important;
  background: url(data:image/png;base64,) !important;
}

img[src*="static/self_default2.png"] {
  padding-left: 70px !important;
  width: 0 !important;
  height: 50px !important;
  background: url(data:image/png;base64,) !important;
}

#mail {
  background: url(data:image/png;base64,) !important;
  width: 16px !important;
  height: 14px !important;
  display: inline-block !important;
  margin: -3px 0 !important;
}

#mail.havemail {
  background-position: bottom !important;
}

#header-img-a {
  outline: none !important;
}

#header-img {
  padding-left: 120px !important;
  width: 0 !important;
  height: 40px !important;
  background: url(data:image/png;base64,) !important;
  position: relative !important;
  top: 2px !important;
}

#header-bottom-right {
  background: url(data:image/png;base64,) !important;
  border: 1px solid #684B30 !important;
  border-right: none !important;
  border-bottom-color: #D3BC9E !important;
  -moz-border-radius: 0 !important;
}

#header-bottom-left {
  background: url(data:image/png;base64,) !important;
}

#sr-header-area {
  background: #CADFFF !important;
  border: none !important;
}

#header {
  border: none !important;
}

#sr-header-area a {
  color: #044B7F !important;
}

#sr-more-link {
  background: #CADFFF !important;
}

#header .redditname a {
  color: #fff !important;
  position: relative !important;
  top: 2px !important;
}

#search input {
  float: right !important;
}

#search:after {
  content: ".";
  display: block;
  height: 0;
  clear: both;
  visibility: hidden;
}

.footer {
  border-top: 1px dotted #bbb !important;
  -moz-border-radius: 0 !important;
}

.side, .spacer {
  background: transparent !important;
}

.flat-list a {
  color: #777 !important;
}

.tagline, .domain, .domain a {
  color: #777 !important;
}

.nohavemail img, .havemail img, .nub {
  display: none !important;
} 

.selected a, #sr-header-area .selected a {
  color: orangered !important;
}

.create .spacer  a[href*="/reddits/create"] {
  background: url(data:image/png;base64,) !important;
}

.submit .spacer a[href*="/Minecraft/submit"] {
  background: url(data:image/png;base64,) !important;
}

.arrow {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACAAAAAdAgMAAAC28IYUAAAADFBMVEUAAAD/flLDw8OHh/+RvRMcAAAAAXRSTlMAQObYZgAAAEdJREFUeNpjYGB1YGBgWsDAgMlgDA1lYFi1qgELIzQ0NERr1apVWBh4DMTCgAP+A1ARIhj/////A7YLk8H8/z/EhZgM3AYCAHlmQpFxxulVAAAAAElFTkSuQmCC) !important;
  width: 15px !important;
}

.midcol {
  min-width: 15px !important;
}

.comment .midcol .arrow {
  margin-bottom: 4px !important;
}

.arrow.up {
  background-position: top right !important;
}

.arrow.upmod {
  background-position: top left !important;
}

.arrow.down {
  background-position: bottom right !important;
}

.arrow.downmod {
  background-position: bottom left !important;
}

.pagename {
  position: relative !important;
  top: -1px !important;
  color: #fff !important;
}

.tabmenu li {
  border-bottom: 1px solid #593D29 !important;
  position: relative !important;
  top: 0px !important;
  display: inline-block !important;
  padding-bottom: 1px !important;
}

.tabmenu a {
  padding-bottom: 1px !important;
  background: url(data:image/png;base64,) !important;
  outline: none !important;
}

.tabmenu .selected a {
  background: url(data:image/png;base64,) !important;
  border-bottom: none !important;
  padding-bottom: 2px !important;
  border-color: #684B30 !important;
}

.sidecontentbox .content, #search input, .linkinfo, .link .usertext .md, .roundfield, textarea, input[type="text"] {
  background: rgba(255,255,255,0.5) !important;
  -moz-border-radius: 0 !important;
  -moz-appearance: none !important;
}

.linkinfo, .link .usertext .md {
  border-color: #808080 !important;
}

.roundfield {
  border: 1px dotted #999 !important;
  padding: 2px !important;
}

textarea, input[type="text"] {
  border: 1px solid #aaa !important;
  padding: 2px !important;
}

.formtabs-content {
  border-top: 4px solid #999 !important;
  padding-top: 0 !important;
}

.formtab li {
  border-bottom: none !important;
}

.formtab .selected a {
  background: #999 !important;
  color: #fff !important;
}

.side .bottom {
  border-top-style: dotted !important;
}

.sidebox .morelink, .sidebox .morelink:hover {
  background: url(data:image/png;base64,) 0 -1px !important;
  -moz-border-radius: 0 !important;
  border: 1px solid #835C37 !important;
/*  text-shadow: 1px 1px 0 #886034 !important; */
}

.sidebox .morelink a {
  color: #fff !important;
}

.sidebox .morelink a:hover {
  text-decoration: underline !important;
}

.child {
  border-color: #aaa !important;
}

.submit .nub {
  width: 50px !important;
  height: 84px !important;
  position: fixed !important;
  bottom: -50px !important;
  top: inherit !important; /* nifty reset for top:-1px */
  right: 20px !important;
  background: url(data:image/png;base64,) !important;
  display: block !important;
  opacity: 0.2 !important;
  z-index: 1 !important;
}

.submit .nub:hover {
  bottom: 0 !important;
  opacity: 1 !important;
}

.expando-button {
  background: url(data:image/png;base64,) !important;
  cursor: pointer !important;
}

.expando-button.collapsed.selftext {
  background-position: 23px 69px !important;
}

.expando-button.collapsed.selftext:hover {
  background-position: 23px 92px !important;
}

.expando-button.collapsed.video {
  background-position: 0 69px !important;
}

.expando-button.collapsed.video:hover {
  background-position: 0 92px !important;
}

.expando-button.expanded.selftext {
  background-position: 23px 23px !important;
}

.expando-button.expanded.selftext:hover {
  background-position: 23px 46px !important;
}

.expando-button.expanded.video {
  background-position: 0 23px !important;
}

.expando-button.expanded.video:hover {
  background-position: 0 46px !important;
}

.markhelp tr[style] {
  background: #e9e9e9 !important;
}

}
