css:
@-moz-document domain('ifanboy.com'), url-prefix('ifanboy.com') {

.comic-archive-title a, .widget-wrap h4 {font-family: Georgia, 'Times New Roman', serif !important;font-size:18px !important;}
.comic-archive-excerpt {font-size:14px !important}
.comic-archive-pull-count {font-family:Verdana, Arial, Tahoma;font-size:12px;}
.comic-archive-pull-count a {color:#fff;}
.home-middle-column .featuredpost {height:auto;}
#content .post p, #content .post ul li, p {font-family: Georgia, 'Times New Roman', serif; font-size: 19px;line-height: 29px;}
#content h1 {font-family: Georgia, 'Times New Roman', serif; font-size: 36px;line-height:38px;text-transform:none}
h3, #comments h3, .related-posts h4 {font-family: Georgia, 'Times New Roman', serif; font-size: 24px;line-height:36px;text-transform:none}
.reply, .reply a {font-weight:normal;text-transform:none;font-size:11px}
.comment {font-family: Georgia, 'Times New Roman', serif;font-size:14px;line-height:24px;}


h4.widgettitle {clear:left;font-family: Georgia, 'Times New Roman', serif;font-size:}
#getyour {display:none}
.singlearticle-content h2 {font-size:50px !important;font-family: Georgia, 'Times New Roman', serif !important;}
body{font-family: Georgia, 'Times New Roman', serif !important;font-size:18px !important;background-image:none;}
.home-left-column {display:none;width:0;}
.home-middle-column {width:100%;float:none;}
.home-right-column {width:30%;}
.home-comic-area {width:100%;margin-bottom:20px}
.singlecomic {float:left;width:48%}
.home-comics {width:100%}
.home-left-content {width:70%;}
#header, #nav, #wrap {width:90%}
#footer {width:100%}
#inner {width:95%}
#home-bottom{width: 100%}
.singlearticle{width:100%}
.home-right-column .sidebarsocial {width:100%}
#home-below-left {display:none}
.singlearticle-content {width:74%;padding-left:10px;}
.sidebar-content #content-sidebar-wrap, .content-sidebar #content-sidebar-wrap {width:100%}
.sidebar-content #content, .content-sidebar #content {width:70%}
#sidebar {width:25%}
#sidebar li, #sidebar-alt li {margin:0 0 10px 0;line-height:24px}


.singlearticle-content {font-size:16px;line-height:20px}
@media screen and (max-width: 1174px) {
     .home-right-column {float:none;width:100%;}
     .home-left-content {width:100%}
     #potw-3 {width:46%;float:left;}
     #botm-3 {width:46%;float:left;}
     .recent-comment {border-bottom:1px solid #e1e1e1;}
}
@media screen and (max-width: 1139px) {
     #header .widget-area {display:none} 
}
@media screen and (max-width: 822px) {
     .singlearticle-content {width:65%}
}
@media screen and (max-width: 622px) {
     .singlearticle-content {width:55%}
     .singlecomic {float:none;width:100%}
     #potw-3 {width:100%;float:none;}
     #botm-3 {width:100%;float:none;}
}
}
