css:
/* Soft7 Windows Theme by AP-GRAPHIK (http://ap-graphik.deviantart.com/art/Soft7-2-0-161602097)
 * Makes the appmenu-button look like windows caption buttons.
 * by Heartripper
 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

 #appmenu-button{
  background: -moz-linear-gradient(top, rgba(255,255,255,.16), rgba(255,255,255,.02)) !important;
  background-origin: padding-box, border-box !important; 
  background-clip: padding-box, border-box !important; 
  border-width: 0 3px 3px 3px !important;
  border-style: solid !important;
  -moz-border-left-colors: rgba(0,0,0,.02) rgba(0,0,0,.15) rgba(255,255,255,.25) !important;
  -moz-border-bottom-colors: rgba(0,0,0,.05) rgba(0,0,0,.15) rgba(255,255,255,.18) !important;
  -moz-border-right-colors: rgba(0,0,0,.02) rgba(0,0,0,.15) rgba(255,255,255,.25) !important;
  box-shadow: none !important;
  padding-bottom: 0 !important;
 }

 #appmenu-button:not([open]){
  border-radius: 0 0 5.5px 5.5px !important;
 }

 #main-window:-moz-window-inactive #appmenu-button{
  -moz-border-left-colors: rgba(0,0,0,.01) rgba(0,0,0,.10) rgba(255,255,255,.21) !important;
  -moz-border-bottom-colors: rgba(0,0,0,.03) rgba(0,0,0,.10) rgba(255,255,255,.14) !important;
  -moz-border-right-colors: rgba(0,0,0,.01) rgba(0,0,0,.10) rgba(255,255,255,.21) !important;
 }

 #appmenu-button:hover:not(:active):not([open]){
  background: -moz-linear-gradient(top, rgba(255,255,255,.30), rgba(255,255,255,.02)) !important;
  background-origin: padding-box, border-box !important; 
  background-clip: padding-box, border-box !important; 
 }

 #appmenu-button:hover:active
 #appmenu-button[open]:hover{
  background: -moz-linear-gradient(top, rgba(255,255,255,.03), rgba(255,255,255,.16)) !important;
  background-origin: padding-box, border-box !important; 
  background-clip: padding-box, border-box !important;
  -moz-border-bottom-colors: rgba(0,0,0,.05) rgba(0,0,0,.15) rgba(255,255,255,.25) !important;
 }

#appmenu-button .button-text { 
  color: transparent !important; 
  text-shadow: none !important; 
  background: url('http://localhostr.com/files/WJmgAXx/brand.png') no-repeat center center !important; 
  padding-right: 14px !important;
}

#appmenu-button .button-menu-dropmarker { 
  display: none !important;
}
