css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sandglaz.com") {

  /* anchors  hover */
  a:hover, .grid .toolbar div:hover {
    color: #149 !important;
  }


  body > header
  , #main-container > footer
  , #fdbk_tab /* feedback */
  , #invites_box /* invite friends button */
  , .sponsered_ads
  /* , #grid-names-span leve menu */
  {
    display: none !important;
  }

  /* seznam gridu */
  .gridlist li {
    position: relative !important;
    top: -1px !important;
    border-bottom-width: 0 !important;
  }
  .gridlist li {
    line-height: 16px !important;
  }
  .gridlist li.sort-hover {
    background: none !important;
    border: dashed 3px #EEEEEE !important;
    border-radius: 3px 3px 3px 3px !important;
  }
  .gridlist li.sort-hover:hover {
    background: none !important;
  }
  .current_grid, .gridlist li:hover {
    background: -moz-linear-gradient(center top , #EEEEEE, #DEDEDE, #DDDDDD, #CCCCCC) repeat scroll 0 0 transparent !important;
    border-radius: 3px 3px 3px 3px !important;
    box-shadow: none !important;
  }
  .current_grid .grid_link {
    color: #4477CC !important;
  }

  /* task ma defaultne border, aby to neskakalo  */
  li.saved.task  {
    border: 0px solid rgba(0, 0, 0, 0) !important;
  }

  /* rozsireni presouvaciho pole */
  .controls {
    width: 32px !important;
  }
  #grid-area-content li .handle {
    width: 15px !important;
    height: 15px !important;
    position: relative !important;
    top: 2px !important;
    left: 1px !important;
  }

}
