css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(music.google.com) {

body {
background-color: #222 !important;
background-image: none !important;
}
.artistViewSongListTop, .songRow td {
    border-bottom: 1px solid #333 !important;
}
#uploading_indicator { display:none!important; }
#syncing_indicator { color:#666!important; }
#invite_friend .icon { background: none !important; width:3px!important }
#invite_friend { color:#666!important; }
#coloredBar { background-color:#111 !important; border-top:#222 4px solid!important; }
#main { background:#222 !important; }
.fade-out-effect { background:none !important; }
.fade-out-content { color:#666 !important; }

#oneGoogleWrapper{
background-color: #111 !important;
}
.search{
background: #333 !important;
-webkit-border-radius: 20px !important;
-moz-border-radius: 20px !important;
border-radius: 20px !important; 
}
.search-button {
background: url("http://www.herrfischerhamburg.de/userstyles/googlemusic/sprites-2.png") no-repeat scroll -348px -285px transparent !important;
}

.search-icon {
    opacity:0.3 !important;
background:none !important;
width:8px!important;
}
#header {
    background-color: #000 !important;
}
#searchBox{
font-weight: normal !important;
}

#insideBox, .fade-out-with-menu{
background: none !important;
}
.label-input-label{
color: #777 !important;
}
.album-art {
    border: #222 1px solid !important;
    padding:0 !important;
-webkit-box-shadow: 0px 0px 2px 2px rgba(0,0,0, 0.3) !important;
-moz-box-shadow: 0px 0px 2px 2px  rgba(0,0,0, 0.3) !important;
box-shadow: 0px 0px 2px 2px  rgba(0,0,0, 0.3) !important; 
}
.browseArtistArtContainer{
background: none !important;
}

#player { background: none !important; background-color: #333 !important;
border-top:#444 2px solid !important;  }
#playing_controls { background: none !important; background-color: #333 !important;  }
#nav { background-color:#000 !important; }
.focus .selected .nav-item {
    background-color: #333 !important;
    color: #dadada !important;
}
.nav-item-container.selected {
    border-color: #000 !important;
}
.nav-item:hover {
   background-color:#111 !important;
}
.browseArtistArtBorder {
    border-color: #000 #000 #000 -moz-use-text-color !important;
    padding:0 !important;
}
.albumImage {
    background-color: #222 !important;
    border: 1px solid #222 !important;
    padding: 0 !important;
-webkit-box-shadow: 0px 0px 2px 2px rgba(0,0,0, 0.3) !important;
-moz-box-shadow: 0px 0px 2px 2px  rgba(0,0,0, 0.3) !important;
box-shadow: 0px 0px 2px 2px  rgba(0,0,0, 0.3) !important; 
}
#playingAlbumArt { border:none !important; }
.albumViewAlbumTitle {
    color:#ED8335 !important;
}
.albumViewArtistTitle {
    font-style:italic !important;
}
.browseArtistTitle .fade-out-content{
    color:#999 !important;
}
.album-name { color:#666 !important; }
.artist-name {
    font-style:italic !important;
}
.nav-section-header {
    border-bottom: 1px solid #333 !important;
}
.album-name-parent {
    margin-top: 5px !important;
}

.artistViewSeparatorRow td {
    border-top: 3px solid #333 !important;
}
.search-border {
    border-bottom: none !important;
    border-left: none !important;
    border-right: none !important;
}
.browseArtistArtBorder { width:40px !important; height:40px !important; background-color:#222 !important; }
.browseArtistAlbumImage { width:40px !important; height:40px !important; background-color:#222 !important; }
.browseArtistArt1, .browseArtistArt2, .browseArtistArt3, .browseArtistArt4 { width:40px !important; }
.song-count, .browseSubtext {
    color: #555 !important;
    
}
.songRow.hover { opacity:0.3 !important; }
.newPlaylistBtn {
    opacity:1 !important;
background: url("http://www.herrfischerhamburg.de/userstyles/googlemusic/sprites-2.png") no-repeat scroll -413px -214px transparent !important;
}
.selectedSong {
    background-color: #333 !important;
}
.selectedSong.hover td {
    background-color: #000 !important;
}
.selectedSong .playing-indicator, .playing-indicator {
    background-image:url("http://www.herrfischerhamburg.de/userstyles/googlemusic/now_playing_selected-2.gif") !important;
}
.goog-slider-track .goog-slider-thumb { background-image:url("http://www.herrfischerhamburg.de/userstyles/googlemusic/now_playing_selected-2.gif") !important;}
#playerSongInfo .goog-flat-button-hover { background-color:#222!important; }
.goog-slider-track .goog-slider-thumb-hover { background: url("http://www.herrfischerhamburg.de/userstyles/googlemusic/sprites-2.png") no-repeat scroll -437px -48px transparent !important; }
.goog-slider-track .goog-slider-thumb { background: url("http://www.herrfischerhamburg.de/userstyles/googlemusic/sprites-2.png") no-repeat scroll -201px -463px transparent !important; }
.logo {
    background: url("http://www.herrfischerhamburg.de/userstyles/googlemusic/sprites-2.png") no-repeat scroll -153px -396px transparent !important;
}
}
