css:
/*Scratchpad background and normal text color*/
.view {
	background-color: #0f0f0f !important;
        color: white !important;
}

/*Font family and size*/
.viewContainer {
	font-family: monospace;
	font-size: 10pt;
}

.ruler_annotation {
	background-color: #000d1a;
	width: 16px;
}

.ruler_annotation_todo {
}

.ruler_annotation_todo_overview {
	background-color: lightgreen;
	border: 1px solid green;
}

.ruler_annotation_breakpoint {
}

.ruler_annotation_breakpoint_overview {
	background-color: lightblue;
	border: 1px solid blue;
}

.ruler_lines {
	background-color: #e8e8e8 !important;
	border-right: 1px solid #e8e8e8 !important;
	text-align: right;
}

.ruler_overview {
	background-color: #e1ebfb;
}

.ruler_lines_even {
	background-color: transparent !important;
        color: #333333 !important;
}

.ruler_lines_odd {
	background-color: white;
}

/*SYNTAX HIGLIGHTING COLORS*/

/*Comments*/
.token_comment {
	color: #9933cc !important;
}

.token_javadoc {
	color: #9933cc !important;
}

/*Strings*/
.token_string {
	color: #54ff02 !important;
}

/*Keywords (function, var, if, for, etc)*/
.token_keyword {
	color: #f75a06 !important;
	font-weight: bold !important;
}

/*Matching bracket*/
.token_bracket_outline {
	outline: 1px #99cc99 !important;
}

.token_bracket {
	color: white;
	background-color: grey;
}

/*Current Line*/
.line_caret {
	background-color: #333333 !important;
}
