css:
#dashboard_index{background:#2C4762 url(http://i55.tinypic.com/of62hv.png) repeat fixed 0% 0% !important;}



#content{background-color:rgba(0,0,0,0.5)!important;}



ol#posts li.notification{background-color: #456179 !important;

border-bottom: 1px solid #2F465C !important;

color: #A8BCCB !important;}



#left_column .dashboard_options_form {

   background: url('http://assets.tumblr.com/images/white_20.png')#1a1a1a;

}



#right_column a#dashboard_switch_blog_menu_current:hover {

   background: transparent;

}



#nav .nav_item.active{

   background-color::#2C4762;

}



#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}

#left_column #posts .post .sub_avatar_inner {

-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;

   background-color::#2C4762;
}



#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{

   background-color:rgba(255,193,37,0.8);

}
