@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain('http://www.tumblr.com/'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {



body {    background: url('http://i922.photobucket.com/albums/ad66/forfucksakesman/Untitled-1-6.jpg') center right #000000 repeat fixed !important;  }ol#posts li blockquote,body.mceContentBody blockquote {margin-left: 0 !important;margin-right: 0 !important;padding-left: 10px !important;border-width: 4px !important;border-color: #b7b7b7 !important;}ol#posts li blockquote blockquote,body.mceContentBody blockquote blockquote {border-color: #b7b7b7 !important;}ol#posts li blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote {border-color: #b7b7b7 !important;}ol#posts li blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote {border-color: #b7b7b7 !important;}ol#posts li blockquote blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote blockquote {border-color: #ffffff !important;}#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {    background: rgba(50,0,0,0.5) !important;  }#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {    background: rgba(50,0,0,0.5) !important;    -webkit-border-radius: 20px;    -moz-border-radius: 20px;    border-radius: 20px;    margin: auto;    padding: 20px 20px;  }h1 {color: #333333;text-shadow: 0 0 3px #ffffff;}.no_posts_found, h1.dashboard_header {color: #ffffff;}img#content_top, img#content_bottom {display: none;}#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {    background: none;  }#right_column .dashboard_nav_item {    background: none;    background-attachment: initial;    background-position-x: 50%;    background-position-y: 100%;    background-origin: initial;    background-clip: initial;    background-color: initial;}#right_column .dashboard_nav_item #hide_radar {background-image: none;background-repeat-x: no-repeat;background-repeat-y: repeat;background-attachment: initial;background-position-x: 0%;background-position-y: 0%;background-origin: initial;background-clip: initial;background-color: transparent;}#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {background: rgba(0,0,0,0.5);}#nav .nav_item.active {    background: rgba(50,0,0,0.5) !important;  }#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {    background: rgba(50,0,0,0.5) !important;  }ol#posts li.notification.alt {    background-color: rgba(0,0,0,0.5);}ol#posts li.notification.first_notification {    background-color: rgba(0,0,0,0.8);}ol#posts li.notification {    background-color: rgba(0,0,0,0.8);    border-bottom: 1px solid #000000;}ol#posts li.notification.last_notification {    border-bottom: 1px solid #000000;}ol#posts li.post .permalink {    background: rgba(50,0,0,0.9);}#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {background: rgba(0,0,0,0.8);border: solid #000000;}ol#posts li.post .post_avatar{border-bottom: 0px solid #0A2939;}#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {border-top: 1px solid #b7b7b7;border-bottom: 1px solid #b7b7b7;}#right_column a#dashboard_switch_blog_menu_current:hover {background: none;}#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {background: none;}form.dashboard_options_form {background-color: rgba(0,0,0,0.7);border-bottom: 1px solid #b7b7b7;border-bottom-left-radius: 10px 10px;border-bottom-left-radius: 10px 10px;border-bottom-right-radius: 10px 10px;border-bottom-right-radius: 10px 10px;border-top-left-radius: 10px 10px;border-top-left-radius: 10px 10px;border-top-right-radius: 10px 10px;border-top-right-radius: 10px 10px;color: #b7b7b7;display: block;font-size: 13px;margin-bottom: 25px;padding: 15px 22px;}form.dashboard_options_form .option_container {border-top: 1px solid #000000;}.no_posts_found, h1.dashboard_header {color: #b7b7b7;}

a.permalink

	{background-color:#000000 !important;

	opacity:50 !important; 

	background-image:none !important;

	border:0px !important}



#nav .nav_item.active, 

#nav .nav_item .nav_menu, 

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel

	{background-color:#3e3e40 !important;

	background-image:none !important;

	border:0px !important}



#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}  #left_column #posts .post .sub_avatar_inner {  -moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;  } 



a.explore_more_tags, a.add_and_remove, a.customize, a.mass_editor 	{background-color:#1d1d1d !important; opacity: 50!important;   	background-image:none !important; 	border:0px !important} 

img.photo 



	{border:0px solid #1d1d1d !important;



	margin:-4px;}



div.selection_nipple,



div.gradient,



div.dashboard_nav_item, 



div.mask_bottom,



div.mask,



div.below_photo,



div.post_photo_post,



div.content_is_media,



a.permalink



	{background-color:#1d1d1d !important;



	opacity:.9 !important; 



	background-image:none !important;



	border:0px !important}

}
