css:
div#backwash_tooltip {
    background: rgba(0, 0, 0, 0.8);
}

form[name="delform"] > blockquote {
    background-color: rgba(0,0,0,0);
}

form[name="delform"] > table {
    width: 0%;
}

.unkfunc {
    color: #006d32;
}

.commentpostername,.postername {
    color: #08B;
    font-weight: 800;
    font-size: 12px;
    background: none;
}

.postertrip {
    background: none;
    color: #8B0;
}

.omittedposts {
    color: #08B;
    font-weight: 700;
    font-size: 11px;
}

.abbr {
    color: #08B;
    font-weight: 700;
}

.filetitle,.replytitle {
    color: #B08;
    background: none;
}

img {
    border-top-right-radius: 3px;
    border-bottom-left-radius: 3px;
    border-top-left-radius: 5px;
    border-bottom-right-radius: 5px;
    box-shadow: 1px 1px 3px #666;
}

img:hover {
    box-shadow: 1px 1px 5px #666;
}

.postblock,.pages {
    background-color: #000;
    opacity: 0.8px;
    border-radius: 3px;
    color: #FFF;
    font-weight: 700;
    border: none;
}

input[type="file"] {
    color: $FFF;
}

form hr {
    display: block;
}

.pages {
    background-color: #EEE;
    border: none;
}

#header {
    margin-bottom: 5px;
    position: static;
    color: #FFF;
}

.postblock {
    background-color: #DDD;
    border: 1px solid #AAA;
    border-radius: 25px 10px / 10px 25px;
}

div#thread_filter.reply {
    background-color: #000;
    color: #FFF;
    margin-top: 2px;
    -moz-border-radius: 4px;
    border: 1px solid #DDD;
    padding: 7px;
}

.reply a,.reply a:visited {
    color: #005480;
    border-radius: 25px 10px / 10px 25px;
}

form a img {
    padding: 0;
}

.pages td {
    background-color: #DDD;
    border: 1px solid #AAA;
    color: #000;
    padding: 3px;
}

td.recaptcha_image_cell {
    padding-left: 1px;
}

span.postertrip {
    color: #0074cc;
    font-weight: 700;
}

#popup {
    border: 0;
    background-color: rgba(0,0,0,0);
    box-shadow: none;
    margin: 0;
    padding: 0;
}

#quickrep {
    background-color: #ededed;
    border-color: #666;
    border-width: 1px;
}

#message font b {
    color: #f0f;
}

font.unkfunc {
    color: #088a11;
}

#recaptcha_image img {
    width: 100%;
    border-color: #000;
    border-style: solid;
}

#recaptcha_response_field {
    width: 100%;
    padding: 0;
}

div.postarea form table {
    border-style: none;
}

#crntBoard {
    box-shadow: 0 0 0 0;
    border-style: none;
}

body form blockquote {
    border-radius: 5px;
    width: 470px;
    opacity: 10px;
    color: #000;
    background-color: rgba(0,0,0,0);
    border-color: #000;
    border-style: solid;
    border-width: 0;
    margin: 1px;
    padding: 6px;
}

#navtop {
    background-color: #262729;
    z-index: 100;
    box-shadow: 0 3px 3px #222;
    background: 0 to(#262729));
    width: 100%;
    font-family: Helvetica, Arial, sans-serif;
    position: fixed;
    text-align: center;
    letter-spacing: 1px;
    font-weight: 700;
    top: 0;
    font-size: 9px;
    border-style: none;
    border-width: 0;
    margin: 0;
    padding: 1px;
}

input.inputtext,textarea.inputtext,#com_submit,tr td input {
    width: 100%;
}

.logo,span#r,.backlink,#recaptcha_reload,#recaptcha_switch_audio_btn,#recaptcha_whatsthis,#recaptcha_logo,#recaptcha_tagline,input[type="checkbox"],.deletebuttons,td.rules,hr,.doubledash,th,table.pages > tbody > tr > td:first-child,input[accesskey="x"],input[accesskey="z"],span[style="left: 5px; position: absolute;"],#footer,table[align="right"],form[name="post"] > div,.bf,td.postblock,#autonoko,#navtopr a,#navtopr,body center font,body form hr {
    display: none;
}

block,* {
    font-size: 11px;
}

#recaptcha_image,.inputtext,input {
    border-radius: 3px;
}

b,td,#header a,#navbot a,#navbotr a,.pages td a {
    color: #FFF;
}

a:link,a:visited,a:active,A:hover {
    text-decoration: none;
    color: #08B;
    font-weight: 700;
}

a.quotejs:hover,.pages td a,span.filetitle {
    color: #005480;
}

span.filesize,tr td small,td.replyhl blockquote,a.quotejs,span.filesize span,td.postblock b {
    color: #000;
}

td.reply,div#backwash_tooltip {
    border-radius: 25px 10px / 10px 25px;
    color: #000;
    border: none;
    padding: 5px;
}

td.replyhl {
    border-radius: 5px;
    color: #000;
    background-color: #fff;
    border-color: #000;
    border-style: solid;
    border-width: 1px;
    background: -webkit-gradient(linear, left top, left bottom, from(#444444), to(#CCCCCC));
}

div.insertedPosts {
    width: 500px;
}

.postarea {
    border: none;
    color: #FFFFFF;
    padding: 0px;
    width: auto;
    height: auto;
    margin: auto 10px 10px auto;
    position: fixed;
    bottom: /0%;
    right: -300px;
    opacity: 0.1;
    -webkit-transition: all 1s ease-in-out;
    background: rgba(0, 0, 0, 0.8);
}

div.postarea:hover {
    border: none;
    opacity: 0.9;
    -webkit-transform: translate(-280px,0);
    background: rgba(0, 0, 0, 0.8);
}

html body {
    font-family: Helvetica, Arial, sans-serif;
}

tbody tr td {
    color: #dbdbdb;
    border-style: none;
    background-color: rgba(0,0,0,0);
    padding-top: 0;
}

td.reply blockquote {
    border-color: #fff;
}

td.reply {
    border-radius: 5px;
    box-shadow: 1px 1px 3px #111;
    text-transform: none;
    opacity: .9;
    background: -webkit-gradient(linear, left top, left bottom, from(#dadada), to(#AAAAAA));
    color: #000;
    border-color: #333;
    border-style: solid;
    border-width: 1px;
}

tr td div {
    margin-top: 1px;
}

textarea.inputtext {
    width: 107%;
    margin-top: -5px;
    margin-left: -2px;
}

img.refreshBtn {
    margin-top: -54px;
    margin-left: -4px;
}

#quick_recaptcha_response_field {
    margin-top: -38px;
    margin-left: -6px;
    width: 133%;
}

body {
    background-color: #dbdbdb;
    background-repeat: repeat;
    background-attachment: fixed;
    background-position: top left;
    background: -webkit-gradient(linear, left top, left bottom, from(#dadada), to(#666666));
}

div.reply {
    background-color: #e0e0e0;
    box-shadow: 1px 1px 3px #111;
    border-radius: 5px;
}

#dragArea div {
    color: #000;
    padding: 0;
    background-color: #dadada;
    border-radius: 5px;
}

#dragArea {
    background-color: #dadada;
    border-radius: 5px;
}

#quick_recaptcha_image img {
    margin-top: -58px;
    margin-left: -5px;
}

#maxchars {
    padding-right: 0;
    width: 106%;
}

tr td input {
    margin-top: -5px;
}

div.reply.navbar3 {
    background: 0 to(#dddddd));
    color: #666;
    border-style: none;
    box-shadow: -1px -1px 3px #111;
}

html body form {
    color: #fff;
    background-repeat: repeat;
    z-index: -30;
    font-family: Helvetica, Arial, sans-serif;
    margin: 0;
    padding: 0;
}

#newform {
    border-style: none;
    border-width: 0;
    margin: 0;
    padding: 0;
    background-color: #dadada;
    height: 100%;
    margin-top: -5px;
    border-radius: 5px;
}

#newform table {
    width: 410px;
}

table.recaptchatable.recaptcha_theme_clean {
    height: 0;
}

tr td small {
    display: none;
}
